css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("muusikoiden.net") {
* { color: #666 !important; font-family: lucida grande !important; line-height: 17px; text-shadow:none !important }
body {background: url("http://www.kolumbus.fi/~w460119/mnet/story08.jpg") top center no-repeat fixed #101010 !important; }

body table { width: 910px !important; margin-left: auto !important; margin-right: auto !important; }
body table table { width: 100% !important;  }
body table img { opacity:.50; filter: alpha(opacity=50); -moz-opacity: 0.5; }

table { border-collapse: collapse }
table, table tr, table td { background:transparent !important; margin:0 !important; padding:0 !important}
td { padding: 4px 0px !important}

hr { display:none } 
a.sidemenu, a.sidemenu:visited { text-transform: none !important; padding: 3px !important }
a.sidemenu:hover { background: #222 !important }

.keskustelu, .keskustelu th, .keskustelu td { border:none !important; background:transparent !important}
.keskustelu td { border-bottom: 1px solid #222 !important }
.keskustelu tr:hover { background: #111 !important}

body table tbody tr td table tbody tr td table tbody tr td table tbody tr td b font { font-family: helvetica !important; font-size: 20px !important; margin: 10px 0 !important; color: #ccc !important; display: block !important; float:left}

blockquote font {color: #ccc !important}
blockquote font i font { color: #777 !important}
.quote { border-left: 4px solid #222; padding: 0 100px 0 10px !important; }
.msg { display:block; margin: 0 150px 0 30px !important; line-height: 18px !important}

.quickmenu { background: none !important; border:none !important }

input, select, textarea { background: #222 !important; color: #ccc; padding: 3px !important; }

.sig { margin: 0 0 20px 70px !important }

a font { color: #888 !important }
a:hover font { color: #c00 !important }
a {text-decoration: none !important;}
a:link, a:visited, a:active { color: #b00 !important; border-bottom:none !important }
a:hover { color:#c00 !important}
}
