css:
/* トップページ */
@-moz-document url(http://yahoo-mbga.jp/),
url(http://yahoo-mbga.jp/?_ref=aff%3Dysm001){
    /* 先ずは要らない物を消去 */
    #slideshow  {display:none !important;}
    #headlines-lay {display:none !important;}
    #main-sec > div[style] {display:none !important;}
    #sub-sec > .bnr1 , .bnr2 ,.bn-lay ,.ydn {display:none !important;}
    .common-mt10 {display:none !important;}
    /* ポップアップ禁止 */
    #overlay {display:none !important;}
    #mypage-popup {display:none !important;}
    
    /* 左右交換 */
    #content-sec {
        position:absolute; top:2%; left:11.8%;
        width:1024px;
    }
    #main-sec {position:absolute; right:60px;}
    #gamenav {
        position:absolute; right:0px;
        margin:0px -10px;
    }
    #sub-sec {position:absolute; left:0px;}
    
    /* フッタ調整 */
    #wapper > #secound-footer-sec,
    #wapper > .footer-bg{
        display:none !important;
    }
}

