/* 全体 */
@-moz-document url-prefix(http://yahoo-mbga.jp/) {
    /* head-bg */
    .head-bg {display:none !important;}
       /* ヤバゲロゴ */
       .logo {display:none !important;}
    
       /* header-lay */
       #header-lay {display:none !important;}
          /* モバコイン残高等 */
          #coin-lay {display:none !important;}
          /* Yahoo!トップへのリンクとログアウト等 */
          #y-lay {display:none !important;}
       /* header-lay終わり */
       
       /* global-lay */
       #global-lay {display:none !important;}
          /* トップ */
          .lv1m-lay {display:none !important;}
          /* マイゲーム */
          .lv10m-lay {display:none !important;}
          /* ゲーム一覧 */
          .lv2m-lay {display:none !important;}
          /* サークル */
          .lv4m-lay {display:none !important;}
          /* 友達 */
          .lv3m-lay {display:none !important;}
          /* マイプロフ */
          .lv11m-lay {display:none !important;}
          /* アバター */
          .lv12m-lay {display:none !important;}
          /* 右の5つのボタン */
          .lv6-lay {display:none !important;}
             /* チャット通知 */
             #growl-chat {display:none !important;}
             /* ミニメ通知 */
             #growl-minimail {display:none !important;}
             /* 伝言板通知 */
             #growl-fmark {display:none !important;}
             /* コメント通知 */
             #growl-diary_comment {display:none !important;}
             /* ゲームお知らせ通知 */
             #growl-game_announce {display:none !important;}
          /* 右の5つのボタン終わり */
       /* global-lay終わり */

       /* top_header_msg */
       .top_header_msg {display:none !important;}
    /* head-bg終わり */
    
    /* secound-footer-sec */
    #secound-footer-sec {display:none !important;}
    
    /* footer-bg */
    .footer-bg {display:none !important;}
    
    /* 広告 */
    a[href*="/friendaff/campaign?_aff_from=game_play"] {display:none !important;}
    .ydn {display:none !important;}
    .common-mt10 {display:none !important;}
}
