css:
@-moz-document domain(cosmicmods.com) {

#header {
background-color: rgb(56, 57, 61);
}

#header {
background: url('http://i.imgur.com/eYY8OQA.png') repeat-x 0 0;
}

body {
-webkit-text-size-adjust: 100%;
-moz-text-size-adjust: 100%;
-ms-text-size-adjust: 100%;
text-size-adjust: 100%;
font-family: 'Droid Sans','Segoe UI',Arial,sans-serif;
color: rgb(20,20,20);
background: rgb(240,240,240) url('http://www.forumvolt.org/styles/flexile/flexile/pattern.png') repeat;
word-wrap: break-word;
}

.footer a, .footer a:visited {
color: rgb(153, 152, 152);
}
    
.footer a, .footer a:visited {
color: rgb(153, 152, 152);
padding: 5px;
display: block;
}

.nodeList .categoryStrip {
font-size: 11px;
color: #FFFFFF;
background: rgba(76,77,81,1) 0%,rgba(71,72,76,1);
padding: 5px 10px;
margin: 3px auto 0;
padding: 5px 10px;
margin: 0;
-moz-border-radius: 5px;
-khtml-border-radius: 5px;
border-radius: 5px;
min-height: 6px;
}

.nodeList .categoryStrip .nodeTitle a {
color: #FFFFFF;
}

a:link, a:visited {
color: rgb(76,77,81);
text-decoration: none;
}

.nodeList .categoryStrip .nodeTitle {
font-weight: bold;
font-size: 10pt;
color: #0C0B0B;
text-shadow: 0 0 0 transparent, 0 0px 0 #f0f7fc;
}

.messageUserBlock {
background: #c2dac7 url('http://www.forumvolt.org/styles/default/xenforo/gradients/tab-selected-light.png') repeat-x bottom;
-webkit-border-radius: 5px;
-moz-border-radius: 5px;
-khtml-border-radius: 5px;
-webkit-border-radius: 5px;
-moz-border-radius: 5px;
-khtml-border-radius: 5px;
border-radius: 5px;
background: #c2dac7 linear-gradient(rgba(255, 255, 255, 0) 95%, rgba(255, 255, 255, .5) 100%);
background: #c2dac7 -webkit-linear-gradient(rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, .5) 0%);
background: #c2dac7 -moz-linear-gradient(rgba(255, 255, 255, 0) 95%, rgba(255, 255, 255, .5) 100%);
background: #c2dac7 -ms-linear-gradient(rgba(255, 255, 255, 0) 95%, rgba(255, 255, 255, .5) 100%);
background: #c2dac7 -o-linear-gradient(rgba(255, 255, 255, 0) 95%, rgba(255, 255, 255, .5) 100%);
position: relative;
}

.textCtrl {
font-size: 13px;
font-family: Calibri, 'Trebuchet MS', Verdana, Geneva, Arial, Helvetica, sans-serif;
color: rgb(73, 73, 73);
background-color: #ffffff;
padding: 3px;
margin-bottom: 2px;
border: 1px solid rgb(0,0,0);
border: 1px solid rgba(0,0,0, .2);
_border: 1px solid rgb(0,0,0);
-webkit-border-radius: 4px;
-moz-border-radius: 4px;
-khtml-border-radius: 4px;
border-radius: 4px;
outline: 0;
}


.messageUserBlock h3.userText {
padding: 6px;
text-align: center;
}

.footer {
border-top: 5px solid rgb(55, 101, 168);
border-bottom: 1px solid rgb(255,255,255);
border-bottom: 1px solid rgba(255,255,255, .05);
_border-bottom: 1px solid rgb(255,255,255);
background: url(http://www.forumvolt.org/rgba.php?r=255&g=255&b=255&a=6);
background: rgba(255,255,255,.025);
progid:DXImageTransform.Microsoft.gradient(startColorstr=#06FFFFFF,endColorstr=#06FFFFFF);
}
  
.navTabs .navTab.selected .navLink {
z-index: 1 !important;
height: 40px;
text-shadow: 0 0 0 transparent, 1px 1px 0 rgba(0,0,0,0.8);
color: rgb(186,194,196) !important;
position: relative;
font-weight: bold;
background: rgb(76,77,81);
border-radius: 4px 4px 0 0;
}
  
.navTabs .navTab.selected .tabLinks {
background: rgb(76,77,81);
background: -moz-linear-gradient(top, rgba(76,77,81,1) 0%, rgba(71,72,76,1) 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,url(http://www.forumvolt.org/rgba.php?r=76&g=77&b=81&a=255)), color-stop(100%,rgb(71, 72, 76)));
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,url(http://www.forumvolt.org/rgba.php?r=76&g=77&b=81&a=255)), color-stop(100%,rgba(71,72,76,1)));
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,url(http://www.forumvolt.org/rgba.php?r=76&g=77&b=81&a=255)), color-stop(100%,rgba(71,72,76,1)));
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(76,77,81,1)), color-stop(100%,rgba(71,72,76,1)))
}
  
.navTabs .navTab.account .navLink .accountUsername {
font-size: 9pt;
margin-top: 1px;
}

#searchBar .textCtrl {
background: url(http://www.forumvolt.org/rgba.php?r=0&g=0&b=0&a=38);
background: rgba(0,0,0,0.15);
border-color: rgb(46,47,41) rgb(86,87,91) rgb(86,87,91) rgb(46,47,41);
border-style: solid;
border-width: 1px;
color: rgb(153, 152, 152);
text-shadow: 0 0 0 transparent, 0 1px 0 rgba(0,0,0,0.8);
-webkit-border-radius: 4px;
-moz-border-radius: 4px;
-khtml-border-radius: 4px;
border-radius: 4px;
}
  
.navTabs .navTab.PopupOpen .navLink {
color: rgb(20,33,88) !important;
text-shadow: 0 1px 0 rgb(79, 127, 179) !important;
}

body {
font: 13px/1.231 arial,helvetica,clean,sans-serif;
}
  
.pageWidth {
max-width: 82% !important;
width: 82% !important;
margin: 0 auto;
}

.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen {
color: rgb(26, 62, 99);
background: rgb(55, 101, 168) url('http://www.forumvolt.org/styles/flexile/flexile/highlight_faint.png') repeat-x top;
-webkit-border-top-left-radius: 3px;
-moz-border-radius-topleft: 3px;
-khtml-border-top-left-radius: 3px;
border-top-left-radius: 3px;
-webkit-border-top-right-radius: 3px;
-moz-border-radius-topright: 3px;
-khtml-border-top-right-radius: 3px;
border-top-right-radius: 3px;
-webkit-border-bottom-right-radius: 0px;
-moz-border-radius-bottomright: 0px;
-khtml-border-bottom-right-radius: 0px;
border-bottom-right-radius: 0px;
-webkit-border-bottom-left-radius: 0px;
-moz-border-radius-bottomleft: 0px;
-khtml-border-bottom-left-radius: 0px;
border-bottom-left-radius: 0px;
}
  
.Menu {
/* background-color: #fff; */
font-size: 11px;
border: 1px solid rgb(55, 101, 168);
border-top: 5px solid rgb(55, 101, 168);
overflow: hidden;
min-width: 200px;
position: absolute;
z-index: 7500;
display: none;
}
  
#AccountMenu .menuColumns a, #AccountMenu .menuColumns label {
width: 115px;
}
  
.blockLinksList a, .blockLinksList label {
color: rgb(87, 116, 158);
}
  
.secondaryContent a {
color: rgb(87,116,158);
}
  
a:link, a:visited {
color: rgb(87,116,158);
text-decoration: none;
}
    
.blockLinksList a, .blockLinksList label {
color: rgb(56, 57, 61);
padding: 5px 10px;
-webkit-border-radius: 5px;
-moz-border-radius: 5px;
-khtml-border-radius: 5px;
border-radius: 5px;
display: block;
outline: 0 none;
}
  
.navTabs .navLink, .navTabs .SplitCtrl {
color: rgb(156,164,166) !important;
text-shadow: 0 0 0 transparent, 1px 1px 0 rgba(0,0,0,0.75);
height: 38px;
line-height: 38px;
font-weight: normal;
}

.navTabs .navTab.PopupClosed .navLink, .navTabs .navTab.PopupClosed .navLink:hover {
color: rgb(153, 152, 152);
}

.navTabs .navTab.PopupClosed .navLink {
color: rgb(153, 152, 152);
}
}
