css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.ecosia.org") {
    body, html, .dropdown-menu, strong {
       color: grey !important;        
       background: #181818; 
    }
       
    input#search {
       background: #181818; 
       color: grey;
       box-shadow: #181818 0px 0px 5px 5px;
       border: 1px solid grey;  
    }
    
    .search-box {
       background: #181818; 
	   box-shadow: #181818 0px 0px 5px 5px;
       border: grey;
    }
    
    .results .search-verticals ul li.vertical a{
        background: #181818 !important; 
        border: 1px solid grey;
    }
    
    .results .search-verticals ul li.vertical a:hover{
        background: #282828 !important; 
        color: grey;
        border: 1px solid grey;
    }
    
    .results .search-verticals ul li.vertical a.active{
       background: #585858;
       border: 1px solid #428BCA;
    }
    
    
    .results .span-middle .search-wrapper, .search-wrapper .gsc-webResult, .results .span-right .statistic {
        border-color: #282828;
    }
    
    .search-wrapper .gsc-webResult .gsc-result:hover {
        border-color: grey;
        color: grey;
        background: #282828;
    }
    
    .ecolink-wrapper, .results .span-middle .ecolink-search-result .promo-meta, .results .span-middle .ecolink-search-result .ecolink-meta .description, .results .span-middle .amazon-product .ecolink-meta .description, .results .span-middle .amazon-product .promo-meta{
        background: #1f4d31;
        color: #599959;
    }
     
    .gsc-results .gsc-cursor-box .gsc-cursor-current-page{
        color: #A90A08 !important;
        background: #383838 !important; 
        border-color: #428BCA !important;
    }
    
    .gsc-results .gsc-cursor-box .gsc-cursor-page {
        color: #428BCA;
        border-color: grey;
        background: #181818; 
    }
    
    .gsc-results .gsc-cursor-box .gsc-cursor-page:hover {
        border-color: grey;
        background: #282828;
    }
    
    .ecosia-ad div.ecosia-ad-description {
        background: black !important;
        color: grey;
    }
    
    #cse .ecosia-ads:first-child, .google .ecosia-ads{
        background: black !important;
        border-color: #383838;
    }
    
    .ecosia-ad .ecosia-ad-title {
        background: black;
        color: grey;
    }
    
    .ecosia-ads, .ecosia-ad div.ecosia-ad-url {
        color: red !important;
        background: black !important;
        border-color: #383838;
        
    }
    
    
    .ecosia-ads .ecosia-ad-tag {
        background: black !important;
        color: lightgreen !important;
    }
       
    .wegreen-rating {
        background: #383838;
        color: grey;
        border: 1px solid green;
    }
    
    .footer{
        background-image: url('http://www.imageshost.de/img/png/a5fcbfca537bff58e4f4f3afbc406c2b.png') !important;
    }

     
}
