css:
/* VanillaForums Blue userstyles.org
 make userstyles.org look more like forum.userstyles.org.
*/@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userstyles.org") { body  { background-color:#f7fff7!important; } }
@-moz-document domain("forum.userstyles.org") {
#Head .Menu  { cursor:default!important; }
#Head #Menu .HomeLink a  { font-size:0!important; padding:0!important; width:28px!important; background:url("http://userstyles.org/favicon.ico") no-repeat center center!important; background-size:24px!important;
}
#Head #Menu > li.UserNotifications+li a  { font-size:0!important; padding:0!important; width:28px!important; border-radius:15px; background:#ff0 url("https://forum.userstyles.org/themes/default/design/favicon.png") no-repeat center center;
}
#Head #Menu > li.UserNotifications+li a:hover  { background-color:#fff; }
#Head #Menu > li.UserNotifications+li a:before  { content: "Mark All Viewed" ;  cursor:default; pointer-events:none; line-height:18px!important; position:absolute; bottom:26px; left:22px; z-index:0; opacity:0; padding:0; margin-left:0px; width:110px; border:solid 1px #111; border-radius:0; font-family:Verdana!important; font-size:12px; font-style:normal; font-weight:300!important; text-align:center; text-shadow:none!important; color:#000; background-color:#bfefff; transition-property:opacity; transition-delay:50ms; transition-duration:100ms;
}
#Head #Menu > li.UserNotifications+li a:hover:before  { z-index:10; opacity:1; transition-delay:600ms; }
.Pager.NumberedPager a, .Pager.NumberedPager span:not(.Ellipsis)
{ font-weight:bold; font-size:13px; text-decoration:none; border:solid 2px #38abe3; padding:0px 4px; background-color:#38abe3; color:#fff; margin:0 2px; }
.Pager.NumberedPager a.Highlight  { background-color:#CFECFF; color:#1E79A7; }
.Pager.NumberedPager a.Next, .Pager.NumberedPager a.Previous  { padding:0px 6px; }
.Pager.NumberedPager span  { cursor:default; }
.Pager.NumberedPager span:not(.Ellipsis)
{ padding:0px 6px; background-color:#f7fff7; color:#aaa; border-color:#bbb; }
#PagerBefore.Pager a.Next:before  { content:"Next"; padding-right:4px; }
#PagerBefore.Pager a.Previous:after  { content:"Prev"; padding-left:4px; }
#PagerAfter.Pager  { position:fixed; left:8px; bottom:8px; z-index:200; }
#PagerAfter.Pager:not(:hover)  { opacity:0.45; }
.Row:not(:hover) #PagerAfter.Pager { opacity:0.25; }
}
@-moz-document url-prefix("http://userstyles.org"), url-prefix("https://userstyles.org") {
/* the forum has no ads, so only to be consistent mind you... */
#obsoletion-message img.screenshot, #post-install-ad, .ad, .install-status>.install-note+div  { display:none!important; }
/* styled for a window viewport width of at least 1125px (for the tables mostly) */
body  { padding-top:56px; max-width:1080px!important; margin-left:40px!important; margin-right:30px!important; }

a[href^="mailto:"]  { background-color:#CFECFF; text-decoration:none; padding-left:2px; padding-right:2px; padding-bottom:1px; }
/* link colors--normal, hover, and visited--in text */
article #long-description a, article #additional-info-text a, article p a, article #long-description a:visited, article #additional-info-text a:visited, article p a:visited
{ color:#247/*!important*/; }
article #long-description a:hover, article #additional-info-text a:hover, article p a:hover
{ color:#b00/*!important*/; }
article #long-description a:visited:hover, article #additional-info-text a:visited:hover, article p a:visited:hover
{ color:#84c/*!important*/; }

/* look like VanillaForums original Blue */
#long-description, #additional-info-text, article p  { font-family:'lucida grande','Lucida Sans Unicode',tahoma,sans-serif!important; font-size:110%!important; }
#main-header  { position:fixed; top:0; left:0; z-index:5; width:100%; min-height:39px; margin:0; border-radius:0; background-color:#38abe3!important; color:#fff!important; }
#main-header h1  { position:relative; bottom:-11px; font-family:Arial,sans-serif; margin-left:78px; }
#main-header a  { text-decoration:none; font-size:24px; color:#fff!important; }
#left-sidebar > *  { box-shadow:0px 0px 2px rgb(153,153,153)!important; border-radius:1px!important; background-color:#cfecff!important; margin-bottom:10px!important; }
#left-sidebar > nav > ul > li:not(:first-child)  { color:#1e79a7!important; background-color:#fff; padding-left:2px; padding-bottom:2px; min-height:16px; max-height:16px; border-top:solid 1px #abdafb; margin-bottom:0!important;
}
#left-sidebar > nav > ul > li:not(:first-child) > a  { text-decoration:none!important; display:block!important; background-color:#e3f4ff; color:#1e79a7!important; padding-left:2px; padding-top:0px; padding-bottom:2px; margin-left:-2px; margin-bottom:-2px;
}
#left-sidebar > nav > ul > li:not(:first-child) > a:hover  { color:#ff0084!important; }
#left-sidebar > nav > ul > li:last-child  { border-bottom:solid 1px #abdafb; }
/* some bits and pieces below derived from "usoMain simpleStyle" by mod_wastrel (user 838) */
.lightboxOverlay { background-color:rgb(6,17,23); }
#advanced-search-submit > button:first-child, #search-submit, #show-advanced-search  { display:none!important; }
#advanced-search  { display:block!important; }
#search-terms  { width:95%!important; float:none!important; }
#left-sidebar > nav > ul > li:first-child > form  { margin-top:2px!important; }
#advanced-search  { border-bottom-width:0!important; }
#left-sidebar > nav > ul > li:first-child+li  { border-top:solid 2px #9FBCCF; }
/*
body > footer > p  { background-color:transparent!important; box-shadow:none!important; padding:0!important; }*/
body > footer > p > a[href="/contact"]
  { text-decoration:none; text-align:center; display:block; cursor:pointer;
    margin:0 0 0 5px; font-size:12px; font-weight:bold; padding:6px 0!important;
    background:#f8f8f8; width:168px!important; max-width:168px!important;
    background:linear-gradient(#ffffff,#cccccc); color:#02475A; text-shadow:0 1px 0 #fff;
    border:solid 1px #999; border-radius:3px; box-shadow:0px 0px 2px #999; }
body > footer > p > a[href="/contact"]:hover  { color:#111; border:1px solid #666; box-shadow:0px 0px 5px #aaa; }
body > footer > p > a[href="/contact"]:focus  { background:#eee; box-shadow:0px 0px 2px #999; }

body > #left-sidebar + footer { position:absolute!important; top:55px!important; }
body > #left-sidebar > nav  { margin-top:39px!important; }
/*for moved contact*/ body > #content-wrapper  { padding-bottom:40px; }/* */
.pagination { cursor:default; position:fixed; bottom:12px; right:10px; }
.pagination .current, .pagination .previous_page, .pagination .next_page, .pagination a
{ font-weight:bold; font-size:13px; text-decoration:none; border:solid 2px #38abe3; padding:2px 4px; background-color:#38abe3; color:#fff; }
.pagination .current { background-color:#CFECFF!important; color:#1E79A7; }
.pagination .previous_page.disabled, .pagination .next_page.disabled { background-color:#f7fff7!important; color:#aaa!important; border-color:#bbb; }
.pagination a.previous_page, .pagination a.next_page { font-size:18px; padding:4px 8px; }
    body:not(:hover) .pagination {opacity:0.4;}
.pagination a:not(:hover), .pagination .current:not(:hover), .pagination .disabled:not(:hover) { opacity:0.5; }
.pagination a.previous_page:hover, .pagination a.previous_page:visited:hover, .pagination a.next_page:hover, .pagination a.next_page:visited:hover
, pagination a:hover, .pagination a:visited:hover { color:#fff!important; }
.pagination a.previous_page {position:fixed!important;left:-26px!important;top:50%!important;}
.pagination a.next_page {position:fixed!important;right:-25px!important;top:50%!important;}
}
@-moz-document url-prefix("http://userstyles.org/styles/"), url-prefix("https://userstyles.org/styles/") {
#social.single-style  { box-shadow:0px 0px 2px rgb(153,153,153); }
#social.single-style:not(:hover) > *  { opacity:0!important; }
section#additional-info  { max-width:800px!important; margin-right:30px!important; }
#show-button  { position:fixed!important; top:35px!important; left:-60px!important; z-index:17!important; cursor:pointer!important; padding:0 5px 1px 0!important; border:none!important; line-height:26px!important; font-family:Consolas,monospace!important; font-size:24px!important; font-weight:bold!important; text-decoration:none!important; text-align:center; background-color:#f31313!important; color:#fff!important;
}
#show-code pre#view-code[style$="block;"]  { position:fixed!important; top:49px; bottom:0; left:0px; right:0; z-index:16!important; overflow:auto!important; white-space:pre!important; max-width:none!important; max-height:100%!important; padding-left:12px!important; margin:auto!important; border-width:0!important; border-left:solid 49px #38abe3!important; background-color:#e7efe7!important;
}
#show-code pre#view-code code#stylish-code  { font-family:Consolas,monospace!important; font-size:15px!important; font-weight:500!important; color:#000!important; }
#show-code *::-moz-selection  { background:#8f7!important; color:#000!important; }
/* control links */
.style-brief-control-links a, #control-panel > a  { text-decoration:none!important; color:#fff; font-weight:bold; padding-left:4px!important; padding-right:4px!important; }
.style-brief-control-links a.edit-link,  #control-panel > a[href*="/edit"]  { background-color:#38abe3!important; }
.style-brief-control-links a.delete-link,  #control-panel > a[href*="/delete"]  { background-color:#ff6347!important; margin-left:-4px; margin-right:-4px; }
.style-brief-control-links a.stats-link,  #control-panel > a[href*="/stats"]  { background-color:#5FA824!important; }
#main-style-info #long-description  { max-height:255px; overflow-y:auto!important; }
#style-settings  { background-color:#d7efd7; padding-top:3px; padding-bottom:3px; border:solid 1px #c7dfc7; max-height:600px; overflow-y:auto; }
/* for now... */
#style-author-info tr:first-child td  { background-color:#BBE2F7; box-shadow:0px 0px 2px #999; }
#style-author-info tr:first-child td:hover  { box-shadow:0px 0px 2px #666; }
#style-author-info tr:first-child td a  { color:#0E69A7; text-decoration:none; display:block; font-weight:bold; padding-left:4px; padding-right:4px; }
#style-author-info tr:first-child td a:hover  { color:#ff0084; }
footer#related  { border-radius:1px; box-shadow:0px 0px 2px #999; background:#cfecff; color:#777; padding:5px 5px; }
footer#related ul li a  { text-decoration:none!important; }
footer#related ul, footer#related ul li  { padding:0!important; }
footer#related ul li:not(:last-child) a  { display:block; }
footer#related ul li a  { padding-top:2px; padding-bottom:2px; color:#1e79a7; }
footer#related ul li a:hover  { color:#ff0084!important; }
footer#related h2  { font-size:110%; color:#1e79a7; font-weight:bold; margin:0; padding:0 0 6px; }
footer#related ul li:not(:last-child)  { background:#e3f4ff; border-top:solid 1px #abdafb!important; padding:0px!important; margin:0; }
footer#related ul li:last-child  { border-top-color:#92B3E5!important; margin:0; padding-top:2px!important; }
#no-discussions > a#start-discussion
{ text-decoration:none; text-align:center; display:block; cursor:pointer; margin:4px 0 0; font-size:15px; font-weight:bold; padding:6px 24px!important; background:#f8f8f8; width:-moz-fit-content; line-height:22px; background:linear-gradient(#ffffff,#cccccc); color:#02475A; text-shadow:0 1px 0 #fff; border:solid 1px #999; border-radius:3px; box-shadow:0px 0px 2px #999;
}
#no-discussions > a#start-discussion:hover  { color:#111; border:1px solid #666; box-shadow:0px 0px 5px #aaa; }
#no-discussions > a#start-discussion:focus  { background:#eee; box-shadow:0px 0px 2px #999; }
ul#discussions, ul#discussions li  { list-style-type:none!important; list-style-position:inside; padding:0!important; }
ul#discussions li a  { padding-top:2px; padding-bottom:2px; color:#1e79a7; font-weight:bold; line-height:22px; max-width:400px!important; }
ul#discussions li a:hover { color:#ff0084!important; }
ul#discussions li, ul#discussions li a  { text-decoration:none!important; }
ul#discussions  { border-radius:1px; background:#cfecff; color:#777; padding:10px!important; box-shadow:0px 0px 2px #999; }
ul#discussions li  { background:#e3f4ff!important; border-bottom:1px solid #abdafb!important; padding:0px 4px!important; }
ul#discussions li:first-child  { border-top:1px solid #abdafb!important; }
ul#discussions li:last-child:not([class])  { background:#cfecff!important; border-bottom-width:0!important; text-align:center!important; }
ul#discussions li:last-child:not([class]) a  { display:inline-block!important; padding:4px 120px 0!important; }
ul#discussions li.no-rating  { list-style-image:
url("data:image/png;base64,") !important; border-left:solid 8px #38abe3; }
ul#discussions li.good-rating  { list-style-image:
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAsAAAALCAYAAACprHcmAAAACXBIWXMAAArrAAAK6wGCiw1aAAAAiElEQVR4nGNgIAcU3jBgBuIiID4JxH+A+AyUz4yiMPGiKicQHwbi/1jwMZA8XHHAKalqIP6PB7fAFTscELoExP/x4HtwxUZbeD8A8X88+CdcsdpKjvNA/B8PRpgss5A1B4j/48GlcMUis5iYgXgLEP/HgveC5DHCGiiYA8SHgfgtlM4hK9JAAABw54kyX7dqZAAAAABJRU5ErkJggg==") !important;
 border-left:solid 8px #72D832; }
ul#discussions li.ok-rating  { list-style-image:
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAsAAAALCAYAAACprHcmAAAACXBIWXMAAArrAAAK6wGCiw1aAAAAg0lEQVR4nGNgIAf8P8XADMRFQHwSiP8A8RkonxlV4X4GTiA+DMT/seBjIHmE4m0M1UD8Hw9uQShewXAJiP/jwfcQiqcxfADi/3jwT4TiNobzQPwfD0YyuZIhB4j/48GlCMUFwGArYNgCxP+x4L0gecywLgDaUAAMwgKGt1A6h6xIAwEABty1b8j477IAAAAASUVORK5CYII=") !important;
 border-left:solid 8px #FFCA03; }
ul#discussions li.bad-rating  { list-style-image:
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAsAAAALCAYAAACprHcmAAAACXBIWXMAAArrAAAK6wGCiw1aAAAAiElEQVR4nGNgIAe8qUlnBuIiID4JxH+A+AyUz4yi8GVuOCcQHwbi/1jwMZA8XPGzGLdqIP6PB7fAFT/0Mb8ExP/x4HtwxbdstT4A8X88+Cdc8RV9+fNA/B8PRph8QUUsB4j/48GlcMVnZPiZgXgLEP/HgveC5DHCGiiYA8SHgfgtlM4hK9JAAAAHbqAzSzgR8gAAAABJRU5ErkJggg==") !important;
 border-left:solid 8px #f00; }
}
@-moz-document url-prefix("http://userstyles.org/users/"), url-prefix("https://userstyles.org/users/") {
#main-article > ul:not([id])  { list-style:none!important; background:#cfecff!important; padding:5px 8px; margin:0!important; display:block; border-bottom:solid 1px #abdafb;
}
#main-article > ul:not([id]) li  { display:inline-block!important; }
#main-article > ul:not([id]) a  { text-decoration:none!important; font-weight:bold; line-height:2.6; padding:5px 10px; margin:0; border:solid 1px #abdafb; border-radius:3px; background:#bbe2f7; color:#1e79a7; display:inline; white-space:nowrap;
}
#main-article > ul:not([id]) a:hover  { background: #f3fcff; }
#main-article > dl > dd > a[href^="http://forum.userstyles.org/messages/add/"]  { width:-moz-fit-content; text-decoration:none; text-align:center; display:block; cursor:pointer; margin:4px 0 10px; font-size:15px; font-weight:bold; padding:8px 18px; background:#f8f8f8; background:linear-gradient(#ffffff,#cccccc); color:#02475A; text-shadow:0 1px 0 #fff; border:solid 1px #999; border-radius:3px; box-shadow:0px 0px 2px #999;
}
#main-article > dl > dd > a[href^="http://forum.userstyles.org/messages/add/"]:hover
  { color:#111; border:1px solid #666; box-shadow:0px 0px 5px #aaa; }
#main-article > dl > dd > a[href^="http://forum.userstyles.org/messages/add/"]:focus
  { background:#eee; box-shadow:0px 0px 2px #999; }

table.author-styles td:last-child  { min-width:160px!important; }
table.author-styles td.date-value  { max-width:76px!important; }
table.author-styles td:last-child > a[href*="/styles/"]  { text-decoration:none!important; color:#fff; font-weight:bold; padding-left:4px!important; padding-right:4px!important; }
table.author-styles td:last-child > a[href*="/edit"]  { background-color:#38abe3!important; }
table.author-styles td:last-child > a[href*="/delete"]  { background-color:#FF6347!important; margin-left:-4px; margin-right:-4px; }
table.author-styles td:last-child > a[href*="/stats"]  { background-color:#5FA824!important; }
table.author-styles tbody tr:nth-child(odd) td { background-color:#e7eff7!important; }
table.author-styles tbody tr td:nth-child(even), table.author-styles tr th:nth-child(even)  { background-color:#d7dfe7!important; }
table.author-styles tbody tr:nth-child(odd) td:last-child  { background-color:transparent!important; }
table.author-styles td a  { text-decoration:none!important; }
table.author-styles td:first-child a  { display:block!important; }
table.author-styles td:first-child a:visited:not(:hover)  { color:#247!important; }
table.author-styles td:first-child a:visited:hover  { color:#5273a5!important; }
table.author-styles td.obsolete a  { font-style:italic!important; color:#ff6347!important; }
table.author-styles td.obsolete:first-child a:visited  { color:#ff6347!important; }
table.author-styles td.obsolete:first-child a:hover  { color:#800!important; }
table.author-styles td,  article .author-styles th  { padding-left:6px!important; padding-right:6px!important; }
table.author-styles thead th  { width:-moz-min-content!important; }
}
@-moz-document url-prefix("http://userstyles.org/users/"), url-prefix("https://userstyles.org/users/"), url-prefix("http://userstyles.org/styles/browse"), url-prefix("https://userstyles.org/styles/browse") {
#social.listing { box-shadow:0px 0px 2px rgb(153,153,153); position:relative; bottom:-1px; }
#social.listing:not(:hover) > * { opacity:0!important; }
/* rating colors on browse */
/* all */
article.style-brief .style-brief-stats > span:last-child  { padding-left:2px; min-width:130px; width:-moz-fit-content; }
article.style-brief .style-brief-stats > span:last-child > img  { display:none; }
/* good */
article.style-brief.good-rating .style-brief-stats > span:last-child  { background-color:#5f5; border:solid 1px #1d1; }
article.style-brief.good-rating .style-brief-stats > span:last-child:after  { content:"Good"; font-weight:bold; }
/* ok */
article.style-brief.ok-rating .style-brief-stats > span:last-child  { background-color:#ff0; border:solid 1px #a0a0a0; }
article.style-brief.ok-rating .style-brief-stats > span:last-child:after  { content:"OK"; font-weight:bold; }
/* bad */
article.style-brief.bad-rating .style-brief-stats > span:last-child  { background-color:#f00; border:solid 1px red; color:white; }
article.style-brief.bad-rating .style-brief-stats > span:last-child:after  { content:"Bad"; font-weight:bold; }
/* none */
article.style-brief.no-rating .style-brief-stats > span:last-child  { background-color:#eee; border:solid 1px #a0a0a0; }
/*  */

.listing-left-info
{ min-height:61px!important; width:163px!important; position:relative!important; border:solid 1px #bbb!important; overflow:hidden!important; height:auto!important; max-height:none!important; }
.listing-left-info.listing-no-screenshot  { border-radius:0!important; }
.listing-left-info .no-screenshots  { margin-top:25px!important; font-size:10px; }
.listing-left-info .screenshot-thumbnail  { text-align:left!important; }
.listing-left-info .screenshot-thumbnail img  { position:absolute!important; max-width:none!important; }
.listing-left-info:hover  { overflow:visible!important; }
.listing-left-info:hover img  { max-width:none!important; max-height:none!important; z-index:4!important; top:-7px; left:-7px; /* VF .FileHover */ background:#fff; padding:6px; border:1px solid #555; box-shadow:0px 2px 10px #000; /* */
}
#main-article article.style-brief { margin-bottom:7px!important; }
#main-article article .style-brief-text
{ margin-left:180px!important; margin-right:50px!important; background-color:#e3f4ff!important; margin-bottom:10px; box-shadow:0px 0px 2px rgb(153,153,153)!important; padding:2px 2px 2px 4px; background-image: linear-gradient(#CFECFF 22px,#ABDAFB 22px,#ABDAFB 23px,transparent 23px);
}
#main-article article .style-brief-text header
{ background-color:transparent!important; z-index:2!important; padding-bottom:2px; }
#main-article article .style-brief-text header a
{ color:#222!important; margin-left:0!important; background-color:transparent!important; padding:0 4px 2px!important; text-decoration:none!important; font-family:Verdana,tahoma!important; color:#1E79A7!important; font-weight:bold;
}
#main-article article .style-brief-text p { max-height:85px; overflow-y:auto!important; margin-top:5px; margin-bottom:5px; }
#main-article article .style-brief-text header a:after { content:"\25bc"; color:rgb(207,236,255); padding-left:4px;}
#main-article article .style-brief-text header a:visited:after { color:#A73FF7; }
}
@-moz-document url-prefix("http://userstyles.org/categories/"), url-prefix("https://userstyles.org/categories/") {
ul#subcategory-list
{ -moz-column-count:3; -moz-column-gap:10px; list-style-type:none; padding:6px 10px!important; border-radius:1px; box-shadow:0px 0px 2px #999; background:#cfecff; color:#777; }
ul#subcategory-list li
{ background:#e3f4ff; padding:0px!important; margin:0; margin-bottom:-1px!important; border-top:solid 1px #abdafb!important; border-bottom:solid 1px #abdafb!important; }
ul#subcategory-list li a  { text-decoration:none!important; display:inline-block; padding-top:2px; padding-bottom:2px; color:#1e79a7; }
ul#subcategory-list li a:hover  { color:#ff0084!important; }
}
@-moz-document url("http://userstyles.org/help"), url("https://userstyles.org/help") {
#main-article > ul:not([id])  { list-style:none!important; background:#cfecff!important; padding:5px 8px; margin:0!important; display:block; border-bottom:solid 1px #abdafb; }
#main-article > ul:not([id]) li  { display:inline-block!important; }
#main-article > ul:not([id]) a  { text-decoration:none!important; font-weight:bold; line-height:2.6; padding:5px 10px; margin:0; border:solid 1px #abdafb; border-radius:3px; background:#bbe2f7; color:#1e79a7; display:inline; white-space:nowrap;
}
