css:
@-moz-document domain("www.clickcritters.com")  {
body {        
        background-color: #000;
        background-image: url('http://i.imgur.com/SvV5hpJ.jpg');
        background-repeat: repeat-x;

    }

    #megaWrapper {
        background-image: none;
    }



    #megaHeader {
        background-image: none;
    }

    #imgHeaderLeft {
        display: block;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        background: url('http://i.imgur.com/kxGsLyj.png') no-repeat;
        width: 1200px;
        height: 125px;
        padding-left: 1200px;
        margin-left: auto;
        margin-right: auto;
    }



    #imgHeaderRight {
        height: 0px;
    }

    textarea {
        font-size: 10pt;
        font-family: Verdana;
    }

    td, p, body {
        font-size: 8pt;
        font-family: Verdana;
        text-align: center;
    }

    a:hover {
        color: #47476B;
        text-decoration: none;
    }

    #megaAd {
        width: 160px;
        background-color: #D2D2E1;
        vertical-align: top;
   margin-left:-50px;
    }

#megaAd iframe {   position:absolute;
  right:0px; padding-top:9px; }

    #megaTopNav, #megaSearch {
        background-color: #47476B;
        background-image: url('http://i.imgur.com/fh9nxQi.jpg');

    }

    #megaSidebar {
        background-color: #47476B;
    }

    #megaUserinfo {
        background-color: #9696b8;
        background-image: url('http://i.imgur.com/rhEZhYV.jpg');
        background-repeat: repeat-x;
    }


    #megaContent,#megaFooter {
        background-color: #D2D2E1; /* If you would like a different color for the background of the content, change this color value here. White is #fff */

    }

    #megaFooter {
        padding-left: 15%;
    }

    #megaBottomLeft {
        background-color: #47476B;
    }

    #megaBottomLeftImg {
        background-image: url('http://i.imgur.com/PPsVf45.png');
    }

    #megaBottomRight {
        width: 160px;
        background-color: #D2D2E1;
    }

    #megaBottomRightImg {
        background-image: url('http://i.imgur.com/3KFnw5T.png')
    }

    h1 {
        background-color: #7575a3;
        border: 2px solid   #47476B;
    }

    #topNavLinks li ul.subnav {
        background: #7575a3;
        border: 2px solid   #47476B;
    }

    #topNavLinks li ul.subnav li {
        margin: 0;
        padding: 0;
        border-top: 1px solid #47476B;
 /*--Create bevel effect--*/
        border-bottom: 1px solid #aeb3d7;
 /*--Create bevel effect--*/
        clear: both;
        width: 170px;
        height: 20px;
    }

    #topNavLinks li ul.subnavi li a {
        color: #ebedfc;
    }

    html #topNavLinks li ul.subnav li a:hover {
        background-color: #47476B;
        color: #ebedfc;
    }

    div.forum {
        color: #47476B;
        background-image: url('http://i.imgur.com/K1lPMjm.jpg');
        background-repeat: repeat-x;
        background-color: #7575a3;
    }

    div.forum div {
        background-image: none;
    }

    div.forum a {
        color: #0f0f2a;
    }

    div.category {
        vertical-align: center;
        background-color: #47476B;
        background-image: url('http://i.imgur.com/fh9nxQi.jpg');
        background-repeat: repeat-x;
    }

    div.category div {
        background-color: #D2D2E1;
        background-image: none;
    }

    #forumtable tbody {
        background-color: #47476B;
    }

    #forumtable tr.unread {
        background-color: #b6b6dc;
    }

    #forumtable tr .off {
        background-color: #d8d8ea;
    }

    #forumtable tr a:hover {
        color: #fff;
        text-decoration: none;
    }

    #forumtable tr:hover {
        background-color: #8585b2;
    }

    form input {
        background-color: #e7e7f8;
    }

    textarea, select, input {
        background-color: #ebedfc;
        font-family: verdana;
    }

    input[type=submit] {
        border-radius: 5px;
        background: linear-gradient(#ebedfc, #a2a2c7);
    }

    #mail_heading tr, #mail_heading td {
        background-color: #ebedfc;
        border: solid 1px #47476B;  
    }
}
