css:
@-moz-document url-prefix("http://direkte.vg.no/studio/norway-chess-2014") {
.days {
  display: none;
}

#feed-module-0 {
  position: fixed;
  width: 28.4%;
  height: 100px;
  bottom: -5px;
  left: 25.8%;
  z-index: 1;
}

#feed-module-0 div.text {
  display: none;
}

#feed-module-0 .well {
  background: #ccc;
}

#feed-module-0 ul {
  float:left;
  list-style-type: none;
  color: #ccc;
}

#feed-module-0 ul li {
 display: inline;
}

#feed-module-0 ul a {
  font-size: 12px;
  color: #222;
  font-style: italic;
  text-decoration: inherit;
}

#feed-module-0 ul li:last-child {
  display: none;
}

.column1 {
  width: 25%;
  margin-right: 5px;
}
  
#feed-module-1 {
  margin-top: 3px;
}

#feed-module-1 .well {
  height: 1000px !important;
}

#feed-module-2 {
  margin-top: 3px;
}

#feed-module-3 {
  display: none;
}

#feed-module-3 iframe {
  height: 0px !important;
}

#feed-module-3 div {
  height: 0px !important;
  display: none;
}

.column2 {
  margin-right: 5px;
}

.column3 {
  float: right;
}

#feed-module-4 {
  display: none; 
}

#feed-module-5 {
  position: relative;
  margin-top: 3px;
  margin-left: 52%;
}

#feed-module-5 .well { 
  height: 100% !important;
}

#feed-module-5 .well p { 
  height: 100% !important;
  visibility: collapse;
  overflow: hidden;
  height: 0px;
}

.chess-moves {
  position: fixed;
  top: 8%;
  height: 1000px !important;
  left: 54.8%;
  width: 24.8%;
  background: #fff;
}

.module.chess_diagram .well {
  background: #ccc;      
}
}
