@charset "utf-8";
/*Autor: skorki */
@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document domain("youtube.com"),domain("www.youtube.com") {
/*strona główna*/
.video-masthead,
#premium-yva,
#header #ad_creative_1,
#alerts {
