}
#yt-masthead-container, #masthead-container, #ticker {
background: #f6dcdf!important; /* Old browsers */
background: -moz-linear-gradient(top, #f6dcdf 0%, #e5959f 50%, #e27481 51%, #f7bdc4 100%)!important; /* FF3.6+ */
