background: -webkit-linear-gradient(top, #f6dcdf 0%,#e5959f 50%,#e27481 51%,#f7bdc4 100%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, #f6dcdf 0%,#e5959f 50%,#e27481 51%,#f7bdc4 100%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top, #f6dcdf 0%,#e5959f 50%,#e27481 51%,#f7bdc4 100%)!important; /* IE10+ */
