-webkit-border-radius: 10px !important;
-moz-border-radius: 10px !important;
border-radius: 10px !important;
}

/*progress*/
#progress { 
background: rgba(210,31,52,0.3) !important;
padding-top: 48px !important;
}
#progress dd, #progress dt {
display: none !important;
}

/*małe przyciski*/
button ,.guide-item.guide-item-selected, .shelves-play , .yt-uix-button-default, #watch-description-expand button, .yt-uix-expander-head button,
.compact-shelf .yt-uix-button-shelf-slider-pager { 
