box-shadow: none !important;
-webkit-border-radius: 15px !important;
-moz-border-radius: 15px !important;
border-radius: 15px !important;
text-decoration: none !important;
}
.guide-item.guide-item-selected:hover, .shelves-play:hover , .yt-uix-button-default:hover, #watch-description-expand button:hover,
 .yt-uix-expander-head button:hover, .compact-shelf .yt-uix-button-shelf-slider-pager:hover{ 
