css:
@-moz-document url-prefix("http://filelist.ro/") {
div:nth-child(1) div:nth-child(1) div:nth-child(2) div:nth-child(3) span:nth-child(3)
{
    padding: 7px 8px 8px 5px;
  	position: absolute;
  	border-bottom-left-radius: 30px;
  	border-top-right-radius: 30px;
  	top: -5px;
	right: 0;
  	z-index: 10;
  	background-size: 200% 100%;
  	background-image: linear-gradient(to right, rgb(50,50,50) 50%, rgb(100, 100, 100) 50%);
    -webkit-transition: background-position .1s ease-in;
    -moz-transition: background-position .1s ease-in;
    transition: background-position .1s ease-in;
}

div:nth-child(1) div:nth-child(1) div:nth-child(2) div:nth-child(3) span:nth-child(3)
{
  	margin-right: 0 !important;
}

div:nth-child(1) div:nth-child(1) div:nth-child(2) div:nth-child(3) span:nth-child(3):hover
{	
	background-position: -100% 0;
}

div:nth-child(1) div:nth-child(1) div:nth-child(2) div:nth-child(3) span:nth-child(3) a:nth-child(1)
{
 	padding: 8px 5px 8px 7px;
  	font-size: 12px;
  	bottom: 2px;
}

.mainheader
{
  	background: #1e1e1e;
  	height: 122px;
  	width: 980px;
  	min-width: 980px;
  	margin: auto;
  	margin-top: 5px;
  	border-top-right-radius: 30px;
}

.statusbar
{
    width: 354px;   
    padding: 0px 10px 2px 10px;
    color: #E0E0E0;
    float: right;
    font-size: 10px;
    background: #1e1e1e;
    /*border-width: 1px;*/
    border-radius: 0;
  	border-width: 0;
    position: relative;
  	border-top-right-radius: 30px;
  	/*border-color: #1fa2e1;*/
}


.cblock-content
 {
    border-width: 1px 1px 0 1px !important;
    border-color: #2471FF;
    border-style: solid;
    padding: 50px 0 10px 0 !important;
    background: #161616;
    position: relative;
    top: -27px;
    width: 978px;
}

#nav
{
  	position: absolute;
	padding: 0;
  	height: 26px;
}

.logo
{
  	position: absolute;
  	top: -20px;
	background: transparent url('http://filelist.ro/styles/images/logo_silver.png') no-repeat;
	margin-top: 5px;
  	max-height: 90px;
	min-width: 250px;
  	padding-bottom: 20px;
}

.status_avatar
{
  	width: 62px;
  	margin: 3px 5px 0 0;
}

.status_avatar img
{
  	width: 62px;
  	height: inherit;
}

#navigation
{
  	position: relative;
  	top: 95px;
  	margin: auto;
  	width: 978px;
  	border-width: 1px 1px 0 1px;
  	border-color: #1fa2e1;
  	border-style: solid;
  	height: 26px;
  	background: rgb(60, 60, 60);
  	z-index: 999;
}

#nav ul li a
{
	padding: 5px 25px 5px 24px;
  	background-image: linear-gradient(to bottom, transparent 50%, #1fa2e1 50%);
  	background-size: 100% 200%;
  	transition: background-position .1s ease-in;
}

#nav lihome
{
  	padding: 5px 26px 5px 26px;
  	background-image: linear-gradient(to bottom, transparent 50%, #1fa2e1 50%);
  	background-size: 100% 200%;
  	transition: background-position .1s ease-in;
}

#nav ul li a:hover, #nav lihome:hover
{
    background-position: 0 -100%;
}

input[value="Cauta!"], input[value="Categorii"], input[value="Login"]
{
  	position: relative;
	background: #1fa2e1;
  	border-radius: 0;
  	height: 27px;
  	font-size: 14px;
  	color: white !important;
  	top: 2px;
  	max-width: 80px;
  	margin-left: 5px;
}

input[value="Cauta!"]:hover, input[value="Categorii"]:hover, input[value="Login"]:hover
{
  	position: relative;
	background: #006291;
  	border-radius: 0;
  	height: 27px;
  	font-size: 14px;
  	cursor: default;
  	max-width: 80px;
}

input[value="Cauta!"]:active, input[value="Categorii"]:active, input[value="Login"]:active
{
  	background: #1fa2e1;
}

#wrapper
{
  	background: black;
}

tbody > tr:nth-child(2n)
{
  	background: rgb(60,60,60);
  	transition: background-color 150ms ease-in;
}

.visitedlinks tr:nth-child(2n + 1)
{
  	background: rgb(45,45,45);
  	transition: background-color 150ms ease-in;
}

tbody > tr:nth-child(n + 2):hover
{
  	background: rgb(100,100,100);
}

.colhead
{
  	background: rgb(30,30,30);
}

#footer
{
  	visibility: hidden;
}

#container
{
  	padding-bottom: 2px;
}

.subheader
{
  	width: 100%;
}

.cblock-header
{
  	position: relative;
    top: -20px;
  	width: 980px;
}

.cblock-headerleft, .cblock-headerright, .cblock-bottom
{
  display: none;
}

#maincolumn
{
  margin: 0 auto 27px auto;
}

#container
{
  	width: 980px;
  	margin: auto;
}

.main
{
  width: 100%;
  border-radius: 0;
}

html, body
{
  	background: #1e1e1e;
}

}

@-moz-document url("http://filelist.ro/login.php") {
.main
{
  width: 550px;
  border-radius: 0;
}

.logo
{
  	position: relative !important;
  	top: 0;
	background: transparent url('http://filelist.ro/styles/images/logo_silver.png') no-repeat;
	margin-top: 5px;
  	max-height: 90px;
	min-width: 250px;
  	padding-bottom: 40px;
}

}
