    #hd_links { display: none !important; }
    #ind_member { display: none !important; }
    #ind_message { display: none !important; }
    #ind_notice { display: none !important; }
    
    #g_head {
        height:20px;
    }
    
    #wrapper {
        position:absolute; top:32px;
    }
    
    #hd_indicator {
        position:absolute; top:1px; left:85%;
        width:90px;
    }
    
