css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.reddit.com/r/Python"), url-prefix("http://www.reddit.com/r/python") {
    .content .thing.stickied a.title, 
    .content .thing.stickied a {
        color: #29664A !important;
    }
    .content .domain a,
    .content .link .tagline {
       color: #444 !important;
    }    
    
    .content .thing .title,
    .content .thing .title:hover,
    .content a,
    .side a {
        color: #0066BF !important;
    }
    .content :visited {
        color: #001f3f !important;
    }
    .thing.link:hover {
        border-left: 2px solid #1E415E !important;
    }
    .thing.link.self:hover {
        border-left: 2px solid #66A8D1 !important;
    }
    .link .entry li a {
        opacity: 1 !important;
    }
    .pagename a:hover {
        background-position: 0px 0px !important;
    }
    .link .usertext .md {
        color: #000 !important;
    }
    button, 
    .fancy-toggle-button .remove,
    button:hover, 
    .fancy-toggle-button .remove:hover {
        border: 1px solid #666 !important;
        padding: 0px 8px 1px !important;
        color: #333 !important;
        box-shadow: 2px 2px #ccc;
    }
    
    #header {
        height: 120px;
    }
    
    #header:after {
        display: none;
    }
    
    #header .tabmenu {
        height: 20px;
        padding-top: 13px;
        padding-left: 14px;
    }
    
    #header .tabmenu li {
        margin: 0;
        padding: 0;
    }
    
    #header .tabmenu li a {
        font-size: 15px;
        padding: 3px 10px;
        margin: 0;
    }
    
    #header-img {
        z-index: 9999;
        background: none;
        height: 50px;
        width: 55px;
        position: absolute;
        top: 20px;
        left: 10px;
    }

    .listing-page .tabmenu li:nth-of-type(3),
    .listing-page .tabmenu li:nth-of-type(4),
    .listing-page .tabmenu li a[href*="/promoted"],
    .listing-page .tabmenu li a[href*="/gilded"] {
        display: inline-block!important;
    }
    
    #header-bottom-right {
        top: 82px;
    }
    
    .content {
        margin-top: 0;
        padding-top: 6px;
    }
    
    h1.redditname {
        display: none;
    }
    
    form#search {
        position: absolute;
        top: 20px;
        z-index: 99999;
    }
    
    #modmail.havemail:before,
    #mail.havemail:before {
        display: none;
    }
    
    div.side {
        margin-top: 11px;
        padding-top: 2px;
    }
}
