css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reactor.cc") {
	html{
		background-color: #000 !important;
	}

	#magnifier {
    margin-top: -29px !important;
    margin-left: -133px !important;
	}
	
	body {
		color: #b8b6b4 !important;
		background-color: #171717 !important;
	}

	.post_content font[color], 
	.post_content *[style*=";color:"],
	.post_content *[style^="color:"],
	.post_content *[style*=" color:"]{
		color:#b8b6b4 !important;
	}
	
	a {
	    color: #ebebeb !important;
	    text-decoration: none !important;
	}

	a:hover {
	    text-decoration: underline !important;
	}

	input[type="text"],input[type="password"],textarea,.post_share_view input {
	    background: transparent !important;
	    border: 1px solid #626366 !important;
	    border-radius: 3px !important;
	    color: #d6d7d8 !important;
	    -moz-appearance: none !important;
	}

	input[type="text"]:selection,input[type="password"]:selection,textarea:selection,.post_share_view input:selection {
	    color: #626366 !important;
	    background-color: #d6d7d8 !important;
	}

	input[type="text"]::-moz-selection,input[type="password"]::-moz-selection,textarea::-moz-selection,.post_share_view input::-moz-selection {
	    color: #626366 !important;
	    background-color: #d6d7d8 !important;
	}

	.ui-widget-content {
	    background-image: none !important;
	    background-color: #3b3938 !important;
	}

	.ui-state-hover,.ui-widget-content .ui-state-hover,.ui-widget-header .ui-state-hover,.ui-state-focus,.ui-widget-content .ui-state-focus,.ui-widget-header .ui-state-focus {
	    background-image: none !important;
	    background-color: #212d3d !important;
	    border: none !important;
	}

	.ui-menu-item a {
	    text-decoration: none !important;
	}

	#background, #container {
	    background-image: none !important;		
	    background-color: #171717 !important;
	}

	#header .description {
	    color: #b8b6b4 !important;
	}

	#header .logo a,#header .logo a:hover {
	    background-image: url("data:image/image/png;base64,") !important;
	}

	.qtip:before,.qtip:after {
	    content: ' ';
	    height: 0;
	    position: absolute;
	    width: 0;
	    border: 5px solid transparent;
	}

	.qtip:before {
	    border-bottom-color: #33425a;
	    border-left-color: #33425a;
	    position: absolute;
	    top: -9px;
	    left: 6px;
	    z-index: 99999;
	}

	.qtip:after {
	    border-bottom-color: #333538;
	    border-left-color: #333538;
	    position: absolute;
	    border-width: 6px;
	    top: -12px;
	    left: 5px;
	    z-index: 99998;
	}

	.qtip-default {
	    background: linear-gradient(to bottom,#33425a 5%,#282f3d 95%) repeat scroll 0 0 rgba(0,0,0,0) !important;
	    border: 1px solid #333538 !important;
	    color: #fff !important;
	}

	#navcontainer {
	    background-image: url("data:image/image/png;base64,") !important;
	    background-repeat: repeat !important;
	}

	#navcontainer ul li {
	    background: none !important;
	}

	#navcontainer ul li a {
	    color: #b8b6b4 !important;
	    text-decoration: none !important;
	}

	#navcontainer ul li span {
	    color: #b8b6b4 !important;
	}

	#navcontainer ul li.current_page_item span,#navcontainer ul li.current_page_ancestor span,#navcontainer ul li#konkurs span {
	    background: url("data:image/image/png;base64,") 0 -75px repeat-x !important;
	    border-radius: 0 !important;
	    border: none !important;
	    box-shadow: none !important;
	}

	#navcontainer ul li.current_page_item a,#navcontainer ul li.current_page_ancestor a,#navcontainer ul li#konkurs.current_page_ancestor a {
	    background: none !important;
	    color: #b8b6b4 !important;
	}

	#navcontainer ul li a:hover {
	    color: #fff !important;
	    text-decoration: none !important;
	}

	#searchBar {
	    background: url("data:image/image/png;base64,") 0 -122px repeat-x !important;
	    color: #fff !important;
	}

	#submenu div.submenuitem,#submenu span,#submenu div.tagname {
	    background: none !important;
	    border-right: 1px solid #000 !important;
	}

	#submenu div.submenuitem:hover,#submenu span:hover,#submenu a:hover {
	    background: none repeat scroll 0 0 #82807c !important;
	}

	#submenu span,#submenu span:hover {
	    background: none !important;
	    border: none !important;
	}

	#submenu div.submenuitem.active,#submenu span.active {
	    background: url("data:image/image/png;base64,") 0 0 repeat-x !important;
	}

	#submenu div.submenuitem.active a,#submenu b,#submenu div.submenuitem.active a:hover,#submenu span.active a:hover {
	    background: none !important;
	}

	#submenu div.submenuitem.active a,#submenu div.submenuitem:hover a,#submenu b,#submenu span:hover a {
	    padding-right: 22px !important;
	}

	#submenu div.submenuitem.active,#submenu span.active,#submenu div.submenuitem:hover,#submenu div.submenuitem.active:hover,#submenu span:hover {
	    margin-right: 0 !important;
	}

	#submenu div.submenuitem:first-child,#submenu span:first-child {
	    background-image: none !important;
	}

	#submenu div.submenuitem a:hover,#submenu span a:hover {
	    text-decoration: none !important;
	}

	#submenu div.submenuitem a.submenu_locker {
	    border-left: 1px solid #000 !important;
	}

	#submenu div.submenuitem a.submenu_locker span {
	    background-image: url("data:image/image/png;base64,") !important;
	    border: none !important;
	}

	#submenu div.submenuitem a.submenu_locker:hover span {
	    background-image: url("data:image/image/png;base64,") !important;
	}

	.icon_mail {
	    background-image: url("data:image/image/png;base64,") !important;
	    width: 29px !important;
	    height: 29px !important;
	    margin-top: -10px !important;
	}

	.icon_mail:hover {
	    background-position: 0 -29px !important;
	}

	.icon_mail.new {
	    background-image: url("data:image/image/png;base64,") !important;
	}

	.icon_mail.new:hover {
	    background-position: 0 -29px !important;
	}

    #content{
    	float: none !important;
    	width: auto !important;
    }
	#sidebar {
		position: absolute !important;
		top:0 !important;
		right:-258px !important;
		background-color: #171717 !important;
	}
	#sidebar::before {
	    content: "";
	    display: block;
	    top: 0px;
	    left: -15px;
	    width: 0px;
	    height: 0px;
	    position: absolute;
	    font-weight: bold;
	    border-right: 15px solid #586263;
	    border-top: 15px solid transparent;
	    border-bottom: 15px solid transparent;
	}
	#sidebar:hover{
		right:0 !important;
	}
	#sidebar:hover::before{
		display: none;
	}

	#searchform {
	    background-image: url("data:image/image/png;base64,") !important;
	}

	#s {
	    background: none !important;
	    border: none !important;
	    border-radius: 0 !important;
	    color: #d6d7d8 !important;
	}

	#sidebar ul li div.sideheader,#sidebar .sideheader {
	    background-image: url("data:image/image/png;base64,") !important;
	    background-repeat: repeat !important;
	    color: #b8b6b4 !important;
	}

	.sidebarContent {
	    background-color: #262626 !important;
	    border: none !important;
	    border-radius: 0 !important;
	    padding: 9px 3px 5px !important;
	}

	.sidebarContent .user img {
	    background: linear-gradient(to bottom,#706c6b 5%,#4e4d4d 95%) repeat scroll 0 0 rgba(0,0,0,0) !important;
	    border-radius: 3px !important;
	    padding: 3px !important;
	}

	.sidebarContent .user-awards {
	    padding: 9px 2px 0 !important;
	    text-align: center !important;
	    background-color: #414141 !important;
	    border-radius: 3px 3px 0 0 !important;
	    border-top: 1px solid #4d4d4d !important;
	}

	#sidebar .sideheader+.sidebarContent .user-awards {
	    border-radius: 3px !important;
	}
    
	.sidebarContent .stars {
	    width: auto !important;
	    text-align: center !important;
	    background-color: #414141 !important;
	    margin: 0 !important;
	    padding: 9px 11px 9px !important;
	}

	.sidebarContent .stars .star-row-0,.sidebarContent .stars .star-row-1 {
	    display: inline-block !important;
	}

	.star-empty,.star-0,.star-1 {
	    background-image: url("data:image/image/png;base64,") !important;
	    background-position: 0 -36px !important;
	}

	.star-0 {
	    background-position: 0 -96px !important;
	}

	.star-1 {
	    background-position: 0 -126px !important;
	}

	.sidebarContent .stars>div:nth-child(2) {
	    padding-top: 9px !important;
	}

	.sidebarContent .stars>div:nth-child(3)[style*="border: black solid 1px"],.sidebarContent .stars>div:nth-child(4)[style*="border: black solid 1px"] {
	    background-color: #222223 !important;
	    border: 1px solid #212121 !important;
	    border-radius: 5px !important;
	    box-shadow: 1px 1px 1px #353535 !important;
	    display: inline-block !important;
	    height: 8px !important;
	    padding: 1px !important;
	    width: 160px !important;
	}

	.sidebarContent .stars>div:nth-child(3)[style*="border: black solid 1px"] .poll_res_bg_active,.sidebarContent .stars>div:nth-child(4)[style*="border: black solid 1px"] .poll_res_bg_active {
	    background: linear-gradient(to bottom,#516179 5%,#2a3547 95%) repeat scroll 0 0 rgba(0,0,0,0) !important;
	    border-radius: 4px !important;
	    height: 8px !important;
	}

	.sidebarContent #rating-text {
	    text-align: center !important;
	    border-radius: 0 0 3px 3px !important;
	    background-color: #414141 !important;
	    padding-bottom: 9px !important;
	}

	.send_private_message.big_button,a.meme_create_link {
	    background: linear-gradient(to bottom,#7e7e7e 5%,#484848 95%) repeat scroll 0 0 rgba(0,0,0,0) !important;
	    color: #fff !important;
	    border-radius: 3px !important;
	    text-shadow: none !important;
	}

	.send_private_message.big_button:before {
	    background: none !important;
	}

	.send_private_message.big_button:hover {
	    background: linear-gradient(to bottom,#969696 5%,#565656 95%) repeat scroll 0 0 rgba(0,0,0,0) !important;
	    text-decoration: none !important;
	}

	.user_icons {
	    background-color: #1a1a1a !important;
	    border-radius: 3px !important;
	    color: #939393 !important;
	    display: inline-block !important;
	    height: 30px !important;
	    line-height: 30px !important;
	    padding: 0 5px !important;
	    margin: 0 5px 5px 0 !important;
	}

	.user_icons:hover {
	    background-color: #97c0e3 !important;
	    color: #3c3d3e !important;
	}

	.user_icons a {
	    background-position: 0 -3px !important;
	    background-repeat: no-repeat !important;
	}

	.new_posts_lnk a {
	    background-image: url("data:image/image/png;base64,") !important;
	}

	.fav_posts_lnk a {
	    background-image: url("data:image/image/png;base64,") !important;
	}

	.mysettings_lnk a {
	    background-image: url("data:image/image/png;base64,") !important;
	}

	.add_toblock_lnk a {
	    background-image: url("data:image/image/png;base64,") !important;
	}

	.add_tofr_lnk a {
	    background-image: url("data:image/image/png;base64,") !important;
	}

	.remove_fromfr_lnk a {
	    background-image: url("data:image/image/png;base64,") !important;
	}

	.user_icons:hover a {
	    background-color: #97c0e3 !important;
	    background-position: 0 -33px !important;
	    color: #3c3d3e !important;
	    text-decoration: none !important;
	}

	#sidebar ul li table img,#sidebar table img {
	    border-color: #97c0e3 !important;
	}

	#sidebar .avatar_holder {
	    height: auto !important;
	    width: 60px !important;
	    margin: 0 0 5px 0 !important;
	    text-align: center !important;
	}

	#sidebar .avatar_holder a img {
	    background: linear-gradient(to bottom,#706c6b 5%,#4e4d4d 95%) repeat scroll 0 0 rgba(0,0,0,0) !important;
	    border-radius: 3px !important;
	    padding: 3px !important;
	}

	#sidebar .tabs_titles,#contentinner .tabs_titles {
	    padding: 0 !important;
	}

	#sidebar .tabs_titles ul,#contentinner .tabs_titles ul {
	    background-image: url("data:image/image/png;base64,") !important;
	    background-position: center bottom !important;
	    background-repeat: no-repeat !important;
	    border-bottom: 1px solid #262626 !important;
	    position: relative !important;
	    float: none !important;
	    height: auto !important;
	    margin: 0 !important;
	}

	#sidebar .tabs_titles ul li,#contentinner .tabs_titles ul li {
	    background: none !important;
	    border-style: solid !important;
	    border-color: #262626 !important;
	    border-width: 0 1px 0 0 !important;
	    color: #cdcac1 !important;
	    cursor: pointer !important;
	    float: left !important;
	    font-size: 10px !important;
	    height: 25px !important;
	    line-height: 25px !important;
	    margin-top: 5px !important;
	    text-transform: uppercase !important;
	}

	#sidebar .tabs_titles ul li a,#contentinner .tabs_titles ul li a {
	    display: inline-block !important;
	    padding: 0 12px 0 !important;
	    background: none !important;
	    border: none !important;
	    text-decoration: none !important;
	}

	#sidebar .tabs_titles ul li.active a,#contentinner .tabs_titles ul li.active a,#sidebar .tabs_titles ul li.active a:hover,#contentinner .tabs_titles ul li.active a:hover {
	    background: none !important;
	    text-decoration: none !important;
	}

	#sidebar .tabs_titles ul li.active,#contentinner .tabs_titles ul li.active {
	    background-color: #4d4b49 !important;
	    border-top-left-radius: 3px !important;
	    border-top-right-radius: 3px !important;
	    color: #fff !important;
	    cursor: default !important;
	    margin-top: 2px !important;
	    padding-top: 3px !important;
	}

	#usertop_week_content,#usertop_month_content,#usertop_blog_week_content,#usertop_blog_month_content,#usertop_blog_overall_content {
	    background-color: #1a1a1a !important;
	    border: 1px solid #4d4b49 !important;
	    overflow: hidden !important;
	    position: relative !important;
	    padding: 10px 0 0 10px !important;
	}

	.user.week_top {
	    padding: 0 10px 10px 0 !important;
	    background-position: 50% 11px !important;
	    background-image: url("data:image/image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAMEAAAABCAYAAABnqsooAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAadEVYdFNvZnR3YXJlAFBhaW50Lk5FVCB2My41LjEwMPRyoQAAABhJREFUOE9j+A8EFjmrQdQoPRoOIzIdAABGVl/b/tNQwgAAAABJRU5ErkJggg==") !important;
	}

	.user.week_top span {
	    vertical-align: bottom !important;
	}

	.user.week_top .userposition {
	    background: none !important;
	    color: inherit !important;
	}

	.user.week_top a {
	    background-color: #1a1a1a !important;
	}

	.user .weekrating {
	    background-color: #1a1a1a !important;
	    color: inherit !important;
	}

	#contentinner {
	    background-color: transparent !important;
	    border: none !important;
	    border-radius: 0 !important;
	    padding: 0 !important;
	}

	#add_post,.postContainer {
	    background-color: #262626 !important;
	    padding: 10px 9px !important;
	    margin: 0 0 5px !important;
	}

	#add_post {    
		background: url("data:image/image/jpeg;base64,") !important;
	    background-position: center bottom !important;
	    background-repeat: no-repeat !important;
	}

	#add_post span.super-tag {
	    background-color: #4c6b23 !important;
	    color: #b0aeac !important;
	    border-radius: 3px !important;
	    border: none !important;
	    box-shadow: 1px 1px 0 0 #000 !important;
	    cursor: pointer !important;
	    margin-bottom: 5px !important;
	    margin-right: 2px !important;
	    overflow: hidden !important;
	    line-height: 19px !important;
	    padding: 0 7px !important;
	    text-overflow: ellipsis !important;
	    white-space: nowrap !important;
	    display: inline-block !important;
	}

	#add_post span.super-tag.active {
	    background-color: #6f884c !important;
	    color: #fff !important;
	    text-decoration: none !important;
	}

	#add_post>div>div.add_post_footer {
	    background: none !important;
	    text-shadow: 1px 1px 0 #000 !important;
	}

	#add_post>div>div a#more_poll {
	    background-image: url("data:image/image/png;base64,") !important;
	}

	#add_post input[type="submit"] {
	    background: linear-gradient(to bottom,#7e7e7e 5%,#484848 95%) repeat scroll 0 0 rgba(0,0,0,0) !important;
	    color: #FFF !important;
	    text-shadow: none !important;
	}

	#add_post input[type="submit"]:hover {
	    background: linear-gradient(to bottom,#969696 5%,#565656 95%) repeat scroll 0 0 rgba(0,0,0,0) !important;
	}

	.article {
	    background-color: #212120 !important;
	    border-color: #212120 !important;
	}

	.article .uhead {
	    height: auto !important;
	}

	.article .uhead .uhead_share {
	    background: none !important;
	}

	.article .uhead .uhead_share span.favorite_link {
	    background-image: url("data:image/image/png;base64,") !important;
	    background-position: 0 -6px !important;
	}

	.article .uhead .uhead_share span.favorite_link.favorite {
	    background-image: url("data:image/image/png;base64,") !important;
	    background-position: 0 -66px !important;
	}

	.article .uhead .avatar {
	    background: linear-gradient(to bottom,#706c6b 5%,#4e4d4d 95%) repeat scroll 0 0 rgba(0,0,0,0) !important;
	    border-radius: 3px !important;
	    padding: 3px !important;
	}

	.taglist a {
	    color: #b0aeac !important;
	    background-color: #384959 !important;
	    border-radius: 3px !important;
	    box-shadow: 1px 1px 0 0 #000 !important;
	    cursor: pointer !important;
	    margin-bottom: 5px !important;
	    margin-right: 2px !important;
	    overflow: hidden !important;
	    line-height: 19px !important;
	    padding: 0 7px !important;
	    text-overflow: ellipsis !important;
	    white-space: nowrap !important;
	}

	.taglist a:hover {
	    color: #fff !important;
	    background-color: #516980 !important;
	    text-decoration: none !important;
	}

	.taglist .tag_fast_delete {
	    margin-bottom: 9px !important;
	    background: url("data:image/image/png;base64,") 0 0 no-repeat !important;
	}

	.taglist .tag_fast_delete:hover {
	    background-position: 0 -11px !important;
	}

	.image {
	    text-align: center !important;
	}

	.post_poll_holder {
	    background: none !important;
	}

	.article .ufoot {
	    padding-bottom: 3px !important;
	    width: 100% !important;
	}

	.commentnum {
	    background-image: url("data:image/image/png;base64,") !important;
	    background-repeat: no-repeat !important;
	    padding-bottom: 3px !important;
	}

	.commentnumDelta {
	    color: #99c407 !important;
	}

	div.vote-plus,div.vote-minus {
	    background-image: url("data:image/image/png;base64,") !important;
	    background-position: 0 -7px !important;
	    width: 16px !important;
	    height: 16px !important;
	}

	div.vote-minus {
	    background-position: 0 -37px !important;
	}

	div.vote-change {
	    opacity: .3 !important;
	}

	.article .post_content_expand,#tagArticle .post_content_expand {
	    width: auto !important;
	}

	#contentInnerHeader {
	    margin: 0 !important;
	}

	.blog_list_item {
	    background-color: #262626 !important;
	    padding: 4px 4px 0 !important;
	    width: 270px !important;
	}

	.comment {
	    background-color: #343435 !important;
	    border-color: #3d3d3e !important;
	    color: #ccc !important;
	}

	.comment.new_my,.comment.new {
	    border-color: #97c0e3 !important;
	}

	.comment .reply-link a,.addcomment span a {
	    color: #898989 !important;
	}

	.comment[style*="background"] {
	    border-color: #99c407 !important;
	}

	.comment .avatar {
	    background-image: url("data:image/image/png;base64,") !important;
	    height: 16px !important;
	    width: 16px !important;
	}

	.pagination .current,.pagination .next,.pagination .prev {
	    border-color: #171717 !important;
	    border-width: 0 3px 0 0 !important;
	    border-style: solid !important;
	    border-radius: 0 !important;
	}

	.pagination a {
	    border: none !important;
	    border-radius: 0 !important;
	}

	.pagination a,.pagination span {
	    border-right: 3px solid #171717 !important;
	    display: block !important;
	    float: left !important;
	    margin: 0 !important;
	    color: #b8b6b4 !important;
	}

	.pagination>* {
	    background-color: #262626 !important;
	}

	.pagination>*:last-child {
	    border: none !important;
	}

	.pagination>*:hover {
	    background-color: #97c0e3 !important;
	    color: #3c3d3e !important;
	    text-decoration: none !important;
	}

	.pagination span:hover {
	    background-color: #262626 !important;
	    color: #b8b6b4 !important;
	}

	.pagination .current, .pagination .current:hover {
	    background-color: #97c0e3 !important;
	    color: #3c3d3e !important;
	}
}
