css:
@-moz-document domain("prodota.ru") {

/*главная*/
html, body
{
background-color: #000;
color: #fff;
}
.main_menu a,.navigation_footer li a{
color: #ccc;
}
.news_preview_wrapper div div a{
color: #ccc !important;
}
.news_list{
background: #1B1B1B;
}
.news_item_wrapper{
color: #999;
padding-top:4px;
margin-top:0px;
}
.news_list_item{
height:28px;
margin-bottom:9px;
}
.news_item_wrapper:hover{
color: #ccc !important;
}
.news_item_wrapper span{
color: #492;
}
.news-innerpage, .news-innerpage:hover{
background-color: #1B1B1B;
border-color: #456;
}
.news-smalltext{
color: #ccc !important;
}
.active_item, .active_item div, .news_preview_wrapper, .block_header,.twtr-hd,.twtr-ft{
background-color: #234 !important;
}
.header_wrapper{
border-color: #456;
}
div#twtr-widget-1 h3, div#twtr-widget-1 h4, div.twtr-doc, div#twtr-widget-1 div.twtr-hd a {
background-color: #234 !important;
}
.block{
background-color: #1b1b1b;
}
.news_main, .comment-text{
color: #ccc;
}
.news-author{
background-color: #234;
color: #ccc;
padding-bottom: 5px;
}
.news-user, .profile_link{
color: #492;
}
.theme_of_day, .flood_of_day, .nobody{
background-color: #ccc;
color: #111;
}
.theme_of_day_zagolovok span,.theme_of_day_features .number_of_comments p, .theme_of_day_features .number_of_views p, .flood_of_day_wrapper span, .nobody_wrapper span, .flood_of_day_features .number_of_comments p, .nobody_wrapper a{
color: #111 !important;
}
.future_game{
background-color: #345 !important;
}
.game_item{
background-color: #454;
}
.future_game b, .game_item b{
color: #fff;
}
.game_item div{
color: #ffc;
}
.game_detail b{
text-shadow: none;
}
.future_game div{
color: #acc;
}
.msg_item{
background-color: #333;
}
.msg_item a{
color: #ccc;
}
.forum_nick_name a{
color: #484;
}
.streamindex{
background-color: #345
}
.reply_who_stream, .reply_info div{
color: #fff;
}
.dotavideo_link:hover{
color: #844;
}
.twtr-tweets{
background-color: #ccc !important;
}
.main{
background-color: #456;
}
.twtr-doc{
border-color: #111;
border-width: 2px;
}
.news_main a{
color: #484;
}
.second_floor_container .games_content .block div b{
color: #ccc;
}
/*форум*/
#content{
background-color: #222;
border-color: #444;
}
.name, .name a{
color: #8b8;
}
span[style="color:black"]{
color: #67a !important;
}
#stat_links li a, .clear{
color: #eee;
}
.ipsSideBlock{
background-color: #222;
border-color: #111;
}
.ipsSideBlock h3{
background: #234 !important;
color: #8a8;
}
h4.statistics_head{
color: #89a
}
.maintitle{
background: #234;
border-color: #111;
}
.maintitle a, .maintitle span{
color: #ccc;
text-shadow:none;
}
.ipsBox{
background-color: #222;
}
.ipb_table tbody tr{
background-color: #222;
}
.col_c_forum h4 a{
color: #481;
}
.table_wrap{
border-color: #111;
}
li.unread a,li.unread{
background-color: #111;
}
div#forum_led_by{
color: #cba;
}
div.error h3, div.error strong,div.error{
background-color: #234 !important;
color: #ccc !important;
}
.unread .altrow, .unread.altrow {
background-color: #111;
}
.post_body{
padding-right: 5px;
padding-left: 13px;
margin-right: 0px;
background-color: #252525;
}
.post{
color: #ccc !important;
}
p.citation{
border-color: #222;
background: #233 ;
}
div.blockquote{
background-color: #243;
border-color: #222;
}
.post_wrap{
background-color: #222;
}
.row2{
background-color: #234;
}
.post_block{
background-color: #252525;
}
div.post_block h3{
background-color: #234;
}
.col_f_icon{
background: #222 !important;
}
#primary_nav, .ipsFilterbar{
background: #222 !important;
border-color:#111;
}
#primary_nav a, .ipsList_inline li a{
color: #cca !important;
text-shadow: none;
}
#primary_nav .active a{
background-color: #234;
}
#forum_load_more{
background-color: #234;
color: #ccc;
}
.ipsPad div{
color: #ccc;
}
.ipsType_pagetitle{
color: #cca;
}
.pagination .back a, .pagination .forward a {
background: #234;
}
.col_c_stats ul li strong, .col_c_stats ul li, .last_post li{
color: #999;
}
.subforums li a, .subforums li{
background-color: #222;
}
.ipsPad{
background-color: #222;
color: #ccc;
}
.ipsType_subtitle{
color: #ccc;
}
table.ipb_table tr td, table.ipb_table td {
border-bottom-color: #111;
}
p.desc{
color: #ccc;
}
.post_controls li a, .ipsButton_secondary{
background: #234;
color: #cca;
border-color: #111;
}
.ipsVerticalTabbed_tabs ul li a, .ipsVerticalTabbed_tabs ul li,
.ipsVerticalTabbed_tabs ul {
background-color: #234;
color: #cca;
border-color: #111;
}
#mlist_content{
margin-left: 3px;
border-color: #111;
}
.ipsType_subtitle strong a{
color: #484;
}
.ipsBox_container{
background-color: #222;
}
.ipsList_withminiphoto li a img{
border-color: #111;
background: transparent;
}
#board_stats span{
color: #444 !important;
}
.userpopup h3, .userpopup a{
background: #234;
}
.userpopup h3{
border: 1px solid #111;
}
.vcard h3 a{
padding-left: 10px;
color: #cca;
}
.user_controls li a{
margin-left: 5px;
}
.user_status.message{
background: #345 !important;
color: #ccc !important;
border-color: #111 !important;
margin-left: 5px;
}
.general_box{
background-color: #222;
}
span.choice{
color: #111;
}
.app label{
color: #cca;
}
.ipsBox{
border-color: #111;
}
.bar{
background-color: #234;
color: #cca;
}
.bbc_spoiler_content{
background: #333 !important;
color: #ccc;
}
pre.prettyprint{
background: #111;
color: #464 !important;
}
.topic_title span{
color: #cca;
}
#user_link_menucontent{
background-color: #234;
}
#user_link_menucontent a{
color: #cca;
text-shadow:none;
}
.last_post li a{
color: #cca;
}
.popupInner h3{
background: #234;
}
.popupInner h3 a{
color: #ccc;
}
h3.maintitle{
color: #cca;
text-shadow: none;
}
.poll_question h4{
background: #234
}
.poll_question ol{
background: #152525;
}
div.message.unspecified{
color: #ccc !important;
background-color: #234 !important;
}
p.progress_bar{
background-color: #ccc;
}
.desc{
color: #ccc;
}
.pagination .pages li.page.active{
background: #234 !important;
color: #cca
}
div#profile_background{
background: #222;
}
div#ipbwrapper div#content.clearfix{
background: #222;
}
ul.topic_buttons li.non_button a, div#footer_utilities{
background: #234 !important;
color:#cca;
}
.list_content a{
color: #cca;
}
#user_latest_status > div{
background-color: #233;
color: #cca;
}
li.tab_toggle.active a{
background-color: #233;
color: #ccc;
}
.unread span{
color: #87CEEB !important
}
}
