css:
@-moz-document domain('wykop.pl'){

body {
  font-size: 1.2rem;
  line-height: 1.6rem;
}

.wrapper {
  max-width: 1440px;
}

#nav .nav li a {
  height: 46px;
}
  
#site {
  margin-bottom: 0;
  padding-top: 44px !important;
}

#footer {
  display: none;
}

#w-button {
  display: none;
}

#fixedBox {
  display: none;
}

div[class='rbl-block overflow m-reset-width force-scalable m-hide'] {
  display: none;
}

div.article.clearfix.preview.dC {display: block !important;}
div.article.clearfix.preview {display: none !important;}


#dyingLinksBox.comments-stream .article.preview .diggbox {display: none !important;}
#dyingLinksBox.comments-stream .article.preview > div > div {display: none !important;}
li.link.dying-links.mark-bg.iC {display: none !important;}

li div[class='article clearfix preview'] {
  display: none;
}

#dyingLinksBox {
  display: none;
}

.rbl-block {
  margin-bottom: 4px;
}
  
.comments-stream > li, .comments-stream > li.hot {
  border-radius: 0;
  margin-bottom:2px;
}

.grid-main {
  margin-right: 318px;
}

.article.preview .media-content {
  float: right;
  width: 180px;
}
.article.preview .media-content img {
  width: 100%;
}
                  
.article.preview .media-content + div {
  margin-left: 0;
  margin-right: 170px;
  padding-top: 10px;
}
                        
.comments-stream .article {
  padding: 0 4px 2px 80px !important;
}

.article.preview h2 {
  font-size: 1.7rem;
}


#rightFooterBox h4, #rightFooterBox ul {
  display: none;
}

#rightFooterBox, #rightFooterBox .button.big {
  bottom: 2px;
}

div[class="row elements actions"] {
  float: right;
  margin-top: -10px;
}

div[class="row elements actions"] .fa{
  display: none;
}
      
.btnReply {
  float: right;
}
  
.row.elements > a, .row.elements > ul > li {
  opacity: 1;
  margin-right: 5px;
  margin-left: 10px;
}
        

.row.elements ul {
  position: absolute;
  top: 9px;
  right: 100px;
}
.sub .row.elements ul {
  top: 0;
}
 
.author p b {
  display: inline;
}
.author p b {
  vertical-align: middle;
}                   
.author p b span {
  font-size: 1.2rem;
}
.author p b * {
  line-height: 18px;
}

.button.mikro *, .button.mikro {
    line-height: 0.8rem;
    font-size: 1.3rem !important;
    padding: 0.2rem 0.2rem !important;
  	border: 0 !important;
}

.sub .wblock {
    padding-left: 6px;
    margin-left: 74px;
}
}
