css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:home") {

body{
	background-image: url("http://i.imgur.com/8TwCVJi.png"), url("http://i.imgur.com/8TwCVJi.png"), url("http://i.imgur.com/8TwCVJi.png"), url("http://image-upload.de/image/Lukc7a/e37f081701.png")!important;
	background-repeat: no-repeat, no-repeat, no-repeat, no-repeat!important;
	background-attachment: fixed, fixed, fixed, fixed!important;
	background-position: 0px -300px, bottom -500px right 200px, top -500px right -200px, top center!important;
	background-color: #151515!important;
}

#launcher{
	height: 75px!important;
	width: 100%!important;
	position: absolute!important;
	top: 0!important;
	background-image: url("http://i.imgur.com/hiMJAEl.png")!important;
	border-bottom: 4px solid #404040!important;
	box-shadow: 0 4px 16px rgba(0, 0, 0, 0.75)!important;
    display: -webkit-flex!important;
    display: flex!important;
    -webkit-flex-direction: row!important;
    flex-direction: row!important;
}

#launcher > button{
	padding: 0!important;
	margin: 0!important;
	color: white!important;
	text-shadow: 0 1px 0 #333, 0 2px 0 #333!important;
	-webkit-flex: 1!important;
	flex: 1!important;
	width: 0!important;
	max-width: 400px!important;
	border-right: 1px solid rgba(10, 10, 10, 0.2)!important;
	border-left: 1px solid rgba(255, 255, 255, 0.2)!important;
}

#launcher > *{
	float: left!important;
}

#restorePreviousSessionSeparator{
	height: 100%!important;
}

#brandLogo{
	height: 200px!important;
	width: 720px!important;
	background-image: url("http://i.imgur.com/4mleTPB.png"), url("http://i.imgur.com/Ik0ISaT.png")!important;
	background-size: auto 100%, auto 100%!important;
	background-position: left center, right center!important;
}

.spacer{
	min-height: 100px!important;
}

#snippetContainer{
	position: fixed!important;
	bottom: 0!important;
	width: 100%!important;
	background-color: rgba(10, 10, 10, 0.5)!important;
}

#snippets{
	margin-bottom: 6px!important;
	margin-top: 15px!important;
}

#searchEngineLogo{
	width: 0!important;
	overflow: hidden!important;
	padding-left: 70px!important;
	margin-right: 70px!important;
	background: url("http://i.imgur.com/SiIy35M.png") no-repeat!important;
	background-size: cover!important;
}

}
