css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://eow.alc.co.jp/search") {
  /* hide */
  /* ## ads */
  #AreaBody > div.clearfix:first-of-type,
  #AreaSponsorSite,
  .mt_20.mb_10,
  .adArea,
  #introduceEowp,
  /* ## header */
  #AreaHeaderLeft,
  #AreaHeaderRight,
  .cl_l,
  .bg_i,
  /* ## footer */
  #AreaLowerLeft,
  #AreaFooter,
  #pagetop,
  .sns,
  /* ## menu */
  #gnav,
  /* ## information */
  #box,
  /* ## charactor */
  #itemsNumber img,
  /* ## right column */
  #AreaUpperRight {
    display: none !important;
  }


  /* layout */
  #AreaBody {
    padding: 0px !important;
    max-width: none !important;
  }
  #AreaUpperLeft {
    position: static !important;
    float: none !important;
  }
  #AreaUpperLeftContainer {
    margin: 0px !important;
    max-width: none !important;
  }
  #resultsList {
    -moz-column-width: 250px !important;
    -moz-column-rule: 1px black dotted !important;
    -webkit-column-width: 250px !important;
    -webkit-column-rule: 1px black dotted !important;
  }
  #resultsList li {
    list-style: none !important;
    display: table !important;
  }
  /* ## top area */
  #sw {
    padding: 5px 0 5px 15px !important;
  }
  /* ## content area */
  /* #### tango button */
  .midashi,
  #resultsList li div {
    padding-left: 20px !important;
  }
  .tango {
    width: 16px !important;
    top: 0.5em !important;
  }
  .tango_btn {
    width: 16px !important;
    height: 16px !important;
  }
}
