css:
@-moz-document 
  url-prefix("https://0.0.0.0:10000")
{

@font-face {
  font-family: "Ubuntu";
  font-style: normal;
  font-weight: 300;
  src: local("Ubuntu Light"), local("Ubuntu-Light"), url("https://themes.googleusercontent.com/static/fonts/ubuntu/v4/_aijTyevf54tkVDLy-dlnD8E0i7KZn-EPnyo3HZu7kw.woff") format("woff");
}

* {
  font-family: 'Ubuntu', 'lucida sans unicode' !important;
}

body {
  background: #480837 !important;
}

a:hover
{
  color: #dd4814 !important;
}


div#container
{
  background: transparent !important;
}

div#menu
{
  background: linear-gradient(180deg, #5c5b56 0%, #3c3b37 100%) !important; /* FF3.6+ */
  border-bottom: #32322d 1px solid !important;
  color: #dfd8c8 !important;
  left: 0px !important; right: 0px !important; top: 0px !important;
}

div#menu > ul > li
{
  border: 1px solid transparent !important;
  border-bottom: none !important;
}
div#menu > ul > li > a
{
  color: #dfd8c8 !important;
  font-size: 95% !important;
  font-family: 'Ubuntu', 'lucida sans unicode' !important;
}
div#menu > ul > li:hover
{
  background: inherit !important;
  border: 1px solid #292927 !important;
  border-bottom: none !important;
  border-top-left-radius: 4px !important;
  border-top-right-radius: 4px !important;  
}
div#menu > ul > li.unused > a
{
  color: #6f6858 !important;
}

div#menu > ul > li#searchbutton,
div#menu > ul > li#gearsstatus,
div#menu > ul > li#logout
{
  border: none !important;
  border-left: 1px solid transparent !important;
  border-right: 1px solid transparent !important;
  border-radius: 0px !important;
}
div#menu > ul > li#gearsstatus
{
  display: none !important;
}


div#menu > ul > li#searchbutton:hover,
div#menu > ul > li#logout:hover
{
  background: linear-gradient(180deg, #f88658 0%, #e56835 100%) !important;
  border: none !important;
  border-left: 1px solid #ad512c !important;
  border-right: 1px solid #ad512c !important;
  border-radius: 0px !important;
}

div#menu > ul > li#searchbutton
{
  background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABYAAAAWCAYAAADEtGw7AAAAmklEQVQ4y2P4//8/Ay0ww6jBA2YwExBPB+LHQPwXih9DxZjINRik8cB/3OAAMYZjE5wONeApENcDsRYU10PF/kPVkGzwY6jmeixy9VC5x+QY/BeqWQuLnBZU7u+gMpiYoHhOq8j7DsTq1E5uMPAan+GkZJAnQHyPWMNJzapsQLwWi+FUKSuwGU61QgjZ8HZql27MQCw1WtATxABL1O/72685iwAAAABJRU5ErkJggg==) !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
}

div#menu > ul > li#searchbutton:hover,
div#menu > ul > li#searchbutton.search-selected  
  
{
  background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABYAAAAWCAYAAADEtGw7AAAAmklEQVQ4y2P4//8/Ay0ww6jBA2YwExBPB+LHQPwXih9DxZjINRik8cB/3OAAMYZjE5wONeApENcDsRYU10PF/kPVkGzwY6jmeixy9VC5x+QY/BeqWQuLnBZU7u+gMpiYoHhOq8j7DsTq1E5uMPAan+GkZJAnQHyPWMNJzapsQLwWi+FUKSuwGU61QgjZ8HZql27MQCw1WtATxABL1O/72685iwAAAABJRU5ErkJggg==),   
                    linear-gradient(180deg, #f88658 0%, #e56835 100%)
  !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
}

div#menu > ul > li div.menuitems-scroll
{
  background: #3c3b37 !important;
  overflow: hidden !important;
  overflow-y: auto !important;
  height: auto !important;
  max-height: 300px !important;
  box-shadow:  3px 4px 9px 0px rgba(0, 0, 0, 0.2) !important;
  border-bottom: #2f2f2c 1px solid !important;
}

div#menu > ul > li ul > li:first-of-type
{
  margin-top: 4px!important;
}
div#menu > ul > li ul > li:last-of-type
{
  margin-bottom: 3px!important;
}

div#menu > ul > li ul > li
{
  background: #3c3b37 !important;
  outline: 1px solid transparent !important;
  border: 1px solid transparent !important;
  border-radius: 0px !important;
  margin: 0px !important;
}
div#menu > ul > li ul > li:hover
{
  background: linear-gradient(180deg, #f88658 0%, #e56835 100%) !important; 
  border: 1px solid #ad512c !important;
  display: block !important;
  width: 243px !important;
}

div#menu > ul > li ul > li div.menuitem
{
  padding: 2px !important;
}

div#menu > ul > li ul > li > a div
{
  color: #dfd8c8 !important;
  font-size: 95% !important;
  font-family: 'Ubuntu', 'lucida sans unicode' !important;  
}

div#searchform{
  background: #43423f !important;
  box-shadow:  3px 5px 10px 1px rgba(0, 0, 0, 0.2) !important;
  border: 1px solid #333 !important;
  border-top: none !important;
  top: 25px !important;
  right: 0px !important;
  font-size: 95% !important;
  margin-top: -1px !important;
}
  
#searchfield_choices
{
  z-index: 1001 !important;
  border: none !important;
  border-bottom: 1px solid #bbb !important;
  background: #fff !important;
  margin-left: 2px !important;
  padding: 5px 0px 5px !important;
  width: 225px !important;
}
  
#searchfield_choices ul li a  
{
  margin-left: 0px !important;
  padding-left: 2px !important;
}

#searchfield_choices li.selected
{
  background: linear-gradient(180deg, #f88658 0%, #e56835 100%) !important; 
}

div#headertitle
{
  overflow: hidden !important;
  padding: 8px !important;
  background-color: #2b011e !important;
  box-shadow: rgba(255,255,255,0.2) 0 1px 1px;
  border-radius: 4px 4px;
  margin: inherit !important;
  width: auto !important;
  margin-top: 36px !important;
  display: inline-block !important;
}

div#headerservers
{
  clear: both !important;
  margin-top: 99px !important;
  display: inline-block !important;
}

div#headerservers li a:hover
{
  color: #dd4814 !important;
}

div#content, #headerwrapper, #headercontent {
  background: transparent !important;
}

div#maincontent table.ui_tabs
{
  box-shadow:  2px 1px 3px 0px rgba(0, 0, 0, 0.1);
  margin: 0px !important;
  padding: 0px !important;
  border-radius: 3px !important;
  border-left: 1px solid #eaeaea !important;
  border-right: 1px solid #eaeaea !important;
}

div#maincontent table.ui_tabs tr
{
  line-height: 1em ! important;
}

div#maincontent table.ui_tabs td.ui_tab
{
  text-transform: uppercase !important;
  padding: 0px !important;
  margin: 0px !important;
  border: 0px !important;
}

div#maincontent table.ui_tabs td.ui_tab td 
{
  background: linear-gradient(180deg, #eaeaea 0%, #f6f6f6 100%) !important;
  font-size: 12px !important;
  font-family: 'Ubuntu', 'lucida sans unicode' !important;
  font-weight: normal !important;
  color: #e04e1d !important;
  padding: 4px !important;
  border: 0px !important;
  margin: 0px !important;
  border-top: 1px solid #eaeaea !important;
  border-bottom: 1px solid #eaeaea !important;
}

div#maincontent table.ui_tabs > tbody > tr > td:first-of-type,
div#maincontent table.ui_tabs > tbody > tr > td:last-of-type
{
  display: none !important
}

div#maincontent table.ui_tabs td.ui_tab table
{
  margin: 0px !important;
}
div#maincontent table.ui_tabs td.ui_tab td a
{
  font-size: 12px !important;
  font-family: 'Ubuntu', 'lucida sans unicode' !important;
  font-weight: normal !important;
  color: #e04e1d !important;
}
div#maincontent table.ui_tabs td.ui_tab td b
{
  font-weight: normal !important;
}

div#maincontent table.ui_tabs td.ui_tab:nth-child(n+3)
{
  border-left: 1px solid #eaeaea !important;

}

div#maincontent table.ui_tabs td.ui_tab td.tabheader
{
  background: linear-gradient(180deg, #fcfcfc 0%, #f3f3f1 100%) !important;
  border-top: 1px solid #eaeaea !important;
  border-bottom: 1px solid #eaeaea !important;
}
div#maincontent table.ui_tabs td.ui_tab:hover td.tabheader
{
  background: #eaeaea !important;
}

#container #content #layout
{
  border: 2px solid orange !important;
  background: white !important;
  border-radius: 6px !important;
}
#container #content #layout #contenttable
{
  background: #d5d1cd !important;
  border: none !important;
  border-radius: 6px !important;
}
#container #content #layout #contenttable #contentblock
{
  background: white !important;
  box-shadow:  3px 0px 4px 0px rgba(0, 0, 0, 0.4);
  border-radius: 0px 0px 0px 0px;
  border: none !important;
}


div#maincontent tr.tabheader
{
  background: #aca69d !important;
  color: #ffffff !important;
}

td#selectedcat.usercolc
{
  padding-bottom: 15px !important;
}

table.icons_table td,
table#mods > tbody > tr > td > table > tbody > tr > td {
  border: 1px solid transparent !important;
  transition: background-color .12s linear, border .12s linear;
}
table.icons_table > tbody > tr > td:hover,
table#mods > tbody > tr > td > table > tbody > tr > td:hover {
  background: #fffbe5 !important;
  border: 1px solid #f2a285 !important;
  border-radius: 2px;
}

/*
  INPUTS
*/

form
{
 background: inherit !important;
 border: none !important;
}

input[type="button"],
input[type="submit"]  
{
  padding: 3px !important;
  border: inherit !important;
  color: #ffffff !important;
  font-size: 13px !important;
  font-weight: normal !important;
  line-height: 17px !important;
  border-radius: 2px !important;
  box-shadow:  1px 1px 2px 0px rgba(0, 0, 0, 0.15);
}

input[type="button"]:active,
input[type="submit"]:active  
{
  background: #962d0c !important;
  color: #aea79f !important;
}

input[type="button"],
input[type="submit"]
{
  background: linear-gradient(180deg, #a39c8a 0%, #8a8371 100%) !important;
  
}

input[type="button"]:hover,
input[type="submit"]:hover
{
  background: #dd4814 !important;
}


.ui_form_end_buttons td:first-of-type input[type="submit"]
{
  background: linear-gradient(180deg, #dd4814 0%, #c03f11 100%) !important;
  
}
.ui_form_end_buttons td:first-of-type input[type="submit"]:hover
{
  background: #b83a0f !important;
}


input[type="text"],
input.ui_textbox
{
   border: 1px solid #ccc1c1 !important;
}

input[type="text"]:focus,
input.ui_textbox:focus
{
   border: 1px solid #dd4814 !important;
   color: #333333 !important;
}



/* *****************
  SIDEBAR 
***************** */

#container #content #layout,
#sidebar,
div#sidebar-info
{
  border: none !important;
  border-radius: 6px !important;
}

div#sidebar-info
{
  background: inherit !important;
  color: #727078 !important;
  padding: 5px 3px !important;
}
div#sidebar-info #sysstats-close,
div#sidebar-info #sysstats-detail
{
  background: inherit !important;
  color: #727078 !important;
  border: none !important;
}
div#sidebar-info label,
div#sidebar-info span
{
  background: inherit !important;
  color: #727078 !important;
  padding-left: 0px !important;
}

div#sidebar-info #sysstats-close a
{
  color: #727078 !important;
  font-size: 85% !important;
}
div#sidebar-info #sysstats-close a:hover
{
  color: #525252 !important;
  font-size: 85% !important;
}

span.graph
{
  border: solid 1px #b2b2b2 !important;
  margin-left: 1px !important;
}
span.graph img:first-of-type
{
  content: url(data:image/png;base64,);
}
span.graph img:last-of-type
{
  content: url(data:image/png;base64,);
}

div#footer
{
  background: inherit !important;
  border: none !important;
  color: #dfd8c8 !important;
}

}
