p {
    font-size: inherit;
    font-family: inherit;
    color: inherit;
    margin: 1em;
    max-width:42em;
    line-height: 1.25em;
}
body#top {
    font-family: Lato, 'Liberation Sans', Helvetica, sans-serif;
    font-size: inherit;
    color: black;
    width: 100%;
    background: whitesmoke;
  overflow:visible;
}
html
{
  overflow:visible;
}
div {
    color: inherit;
    font-size: inherit;
    font-family: inherit;
}
div#divLeft {
    width: 20%;
    min-width: 214px;
    height: auto !important;
    margin-top: 0;
    position: inherit;
    top: inherit;
    left: inherit;
}
ul.awstoc {
    padding: 1em;
}
li.awstoc.closed {
    background-color: inherit;
}
li.awstoc {
    font-size: .8rem;
}
li.awstoc.opened > ul.awstoc {
    padding-top: .25em;
}
li.awstoc.opened {
    padding-top: 1em;
}
div#divRight {
    width: 80% !important;
    height: auto !important;
}
body#top > div {
    position: inherit;
    top: inherit;
    left: inherit;
    float: left;
    box-sizing: border-box;
}
div#divHeader {
    min-width: initial;
    height: auto;
}
div#divHeader > form:first-child > div {
    position: initial;
    left: inherit;
    top: inherit;
    margin: 0;
}
div#divHeader > form:first-child {
    display: flex;
    width: 100%;
}
div#ad {
    vertical-align: middle;
    width: auto;
    width: 25%;
    padding: 1em;
}
img#adi {
    vertical-align: middle;
    height: 100%;
    margin: 0;
}

code {
    font-size: .9em;
    background: rgba(0,0,0,.025);
    margin: auto .23em;
}
p.simpara {
    margin-bottom: 1em;
}
li {
    margin: .5em;
    line-height: 1.25em;
}
h2 {
    font-size: 1.4rem;
    padding: 0;
    margin-top: 1em;
  margin-bottom:.5em;
}



a#adl {
    font-size: inherit;
    font-weight: inherit;
}
div#txt {
    width: 30%;
    vertical-align: middle;
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    line-height: 1.6em;
    padding: 1em;
}
div#sfrm {
    width: 45%;
}
div#srv {
    line-height: 1em;
}
div#txt div#srv {
    display: inline-block;
}
div#txt div#gd {
    display: inline-block;
}
div#divContent {
    padding: 1em;
}
.itemizedList p.title {
    margin-top: 1.6em;
}
}
