css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

.global-nav-inner, .PromptbirdPrompt,
.DashboardProfileCard, .ProfileHeading-content,
.ProfileHeading-toggleItem, .ProfileHeading-toggle,
.stream-item:not(.no-header-background-module)
{
background:#222222 !important;
}
button
{
background:#0089FD !important;
}
.Trends, .ProfileCanopy-navBar, .tweet:hover, .WhoToFollow,
.home-tweet-box, .LiveVideo-tweetBox, .RetweetDialog-commentBox,
.ProfileNav-item--userActions, .DMInboxItem, .DMInboxItem:hover,
.DMActivity-header, .DMConversation-scrollContainer,
.module .flex-module
{
background-color:#222222 !important;
}
.DirectMessage-text, html, #page-outer
{
background-color:#111111 !important;
}
.global-nav .search-input, .tweet-box
{
background-color:#000000 !important;
}
.new-tweets-bar
{
background-color:#0089FD !important;
}
.EdgeButton--primary
{
background-color:#0089FD !important;
}
.DirectMessage-text
{
border: 1px #FFFFFF solid !important;
}
.stream-item + .stream-item.separated-module:not(.no-header-background-module), .stream-item.separated-module + .stream-item:not(.no-header-background-module), .PromptbirdPrompt-streamItem.separated-module + .stream-item:not(.no-header-background-module), .new-tweets-bar
{
border-top: 1px #0089FD solid !important;
}
.recap-header, .DashboardProfileCard-bg,
.DMInboxItem, .DMActivity-header,
.new-tweets-bar
{
border-bottom: 1px #0089FD solid !important;
}
.stream-item:not(.no-header-background-module)
{
border-left: 1px #111111 solid !important;
}
.stream-item:not(.no-header-background-module)
{
border-right: 1px #111111 solid !important;
}
.ProfileAvatar
{
border: 5px #0089FD solid !important;
}
.ProfileHeading-content, .global-nav .search-input,
.WhoToFollow, .DashboardProfileCard, 
.Trends, .tweet, .module .flex-module
{
border: 1px #0089FD solid !important;
}
.Trends .trends-container
{
border: 1px #222222 solid !important;
}
.DirectMessage--received .DirectMessage-text
{
background-color: #0000FF !important;
}
.DirectMessage-text
{
border-radius: 0 !important;
}
.ProfileHeading-content, .global-nav .search-input,
body:not(.edge-design) .EdgeButton, .WhoToFollow,
body:not(.edge-design) .top-timeline-tweetbox .tweet-user
{
border-radius: 0 !important;
}
.new-tweets-bar
{
font-size: 16px !important;
}
.new-tweets-bar
{
font-weight: bold !important;
}
.new-tweets-bar
{
color: #222222 !important;
}
html, body, .ProfileCardStats-statLabel, h1, h2, h3,
.ProfileNav-label, .fullname, .text, .TweetTextSize,
.global-nav .search-input, .DMActivity-header, .Footer-item,
.ProfileHeaderCard-joinDate, .DMActivity-title, b,
ProfileHeaderCard-joinDateText, .DMInboxItem-snippet,
.DirectMessage-footer, .trend-item-stats, .trend-location,
.js-retweet-text, label, button
{
color:#FFFFFF !important;
}
.u-textUserColor, .u-textUserColorHover:hover, .u-textUserColorHover:hover .ProfileTweet-actionCount, .u-textUserColorHover:focus, .recap-header,
.account-group .username, .time, .time a, .created-via
{
color:#0089FD !important;
}

}
