css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.google.com/voice") {

    /* SEARCH BAR */
    
    /* APP BAR */
    .gc-appbar {
        /* background: #3a3d3d; */
        background: #fff;
        border: none; 
        border-bottom: 1px solid #ccc;
    }
    
    .gc-appbar-label {
        color: #61b73f !important;
    }
    
    /*
    .gc-appbar .goog-flat-menu-button {
        background: #3a3d3d;
        border: 1px solid #222;
        color: #ddd !important;
    }
    
    .gc-appbar .jfk-button {
        background: #3a3d3d;
        border: 1px solid #222;
        color: #ddd !important;
    } 
    */
    
    /* SIDEBAR */
    /*.goog-splitpane-first-container {
        width: 110px !important;
        box-sizing: border-box;
    }
    
    #gc-sidebar{
        box-sizing: border-box;
        width: 100%;
        background: white;
        border-right: 1px solid #ccc;
        border-top: 1px solid #ddd;
    }
    
    #gc-inbox-sidebar-main {
        margin: -18px 0px 0px 20px;
        width: 100%;
        border: none;
        outline: none;
    } */
    
    #gc-sidebar .jfk-button {
        background: #61b73f;
        border: none;
        color: white !important;
    } 
    
    
    /* MESSAGES */
    .gc-inbox-view-main{
        background: #e4e4e4;
        padding-top: 0.5%;
        padding-left: 0.5%;
        border: none;
        box-shadow: inset 1px 1px 3px rgba(0,0,0,0.1);
    }
    
    .gc-message{
        margin: 0% 0.5% 0.5% 0%;
        width: 40%;
        display: block;
        float: left;
        background: #fff;
        border: 1px solid #fff;
        border-radius: 2px;
        box-shadow: 0px 1px 3px rgba(0,0,0,0.2);
        transition: border 0.2s linear;
    }
    
    .gc-message:hover, .gc-message:active{
        border: 1px solid #61b73f;
    }
    
    .gc-message-sms-reply {
        width: 100%;
    }
    
    .gc-message-sms-reply, .gc-message-sms-reply .goog-inline-block, .gc-message-sms-reply textarea, .gc-message-sms-reply .gc-message-sms-ctrl {
        width: 100%;
    }
    
    .gc-message-sms-reply .jfk-button {
        width: 30%;
    }
    
    .gc-message-message-display{
        height: 230px; 
        overflow-y: auto;
    }
    
    @media (max-width:1200px) {
        .gc-message{
            width: 97%;
        }
    }
    
    /* MESSAGE TITLE/HEADER */
    
    .gc-message-tbl-metadata {
        padding-left: 10px !important;
    }
    
    .gc-message-tbl-portrait {
        box-sizing: border-box;
        width: 48px !important;
        height: 48px !important; 
    }
    
    .gc-message-portrait {
        margin-top: 2px;
        width: 48px !important;
        height: 48px !important;
    }
    
    .gc-message-portrait img {
        width: 48px !important;
        height: 48px !important;
        border: 1px solid #ccc;
        border-radius: 5px;
        box-shadow: 0px 2px 0px rgba(0,0,0,0.1);
    }
    
    .gc-message-name-link{
        width: 100%;
        display: block;
        font-size: 1.2em;
        font-weight: 200 !important;
    }

    .gc-message-name span, .gc-message-to, .gc-message-icon-11, .gc-message-icon-10 {
        display: none;
    }
    
    .gc-message-time-row span, .gc-message-type {
        margin: -2px 0px -2px 0px;
        color: #aaa !important;
        font-size: 0.9em;
    }
    
    .gc-message-time {
        margin: 0px;
    }    
    
    .gc-message-sms-row{
        margin-bottom: 5px;
        padding: 8px;
        background: #f9f9f9;
        border-radius: 3px;
        border: 1px solid #eee;
    }
    
    .gc-message-sms-from{
        width: 100%;
        display: block;
        font-size: 0.9em;
        font-weight: 200;
        color: #888;
        text-decoration: underline;
    }
    
    .gc-message-sms-time{
        float: right;
        font-size: 0.9em;
        font-weight: 200;
        color: #888;
    }
    
    /* FOOTER */
    .gc-footer-inbox{
        margin: 20px 0px 0px 0px;
        widtH: 100%;
        display: inline-block;
    }
    
    .gc-user-tip{
        display: block;
        background: #ddd;
    }
    
    .gc-user-tip .goog-inline-block{
        color: #333;
        background: none;
        border: none;
    }
   
}
