css:
 @-moz-document domain(hipchat.com) {

body.chat a {
  color: #8E9B6B;
}

/* main -- start */
body.chat #main_chat {
}

/* chat input */
body.chat #main_chat .input {
  border-top-width: 3px;
  border-top-color: #E8E8E8;
  background-color: #202020;
}

body.chat #main_chat .input .textwrapper {
  padding: 0;
  border-width: 0;
  background-color: transparent;
}

#message_input {
  width: 96%;
  height: auto;
  background-color: transparent;
  color: #E8E8E8;
}

body.chat #main_chat .input #upload_button {
  width: 25px;
  height: 25px;
  border-width: 0;
  border-radius: 50%;
  background-image: -webkit-linear-gradient(top, #FFFFFF 0%, #F3F3F3 100%);
  background-image: linear-gradient(top, #FFFFFF 0%, #F3F3F3 100%);
  background-color: #FFFFFF;
}

body.chat #main_chat .input #send_message_button {
  display: none;
}

/* chats */
body.chat #main_chat #chats {
  height: 93.6%;
  border-bottom-width: 0;
}

#chats div.dateDivider {
  border-top-color: #636363;
  background-color: #202020;
  background-image: none;
  color: #EDEC9C;
}

#chats div.them,
#chats div.me {
  background-color: #202020;
  color: #E8E8E8;
}

#chats div.welcomeMessage {
  border-top-color: #636363;
  background-color: #202020;
  color: #EDEC9C;
}

#chats td.nameBlock,
#chats div.me td.nameBlock,
#chats div.welcomeMessage td.nameBlock {
  background-color: #202020;
  border-top-width: 0;
  border-right-width: 0;
}

#chats div.me td.nameBlock p {
  color: #636363;
}

#chats div.welcomeMessage td.nameBlock p {
  color: #EDEC9C;
}

#chats td.messageBlock,
#chats div.me td.messageBlock,
#chats div.welcomeMessage td.messageBlock {
  border-left-color: #636363;  
}

#chats div.chatBlock {
  border-top-color: #636363;  
}

#chats span.atTag {
  padding: 0;
  border-width: 0;
  background: transparent;
}

#chats span.atTagMe {
  color: #DD843E;
}
/* main -- end */

/* sidebar -- start */
#sidebar {
  width: 165px;
  border-left-color: #636363;
}

/* roster */
#roster {
  background-color: #202020;
}

#roster div.member {
  background-color: #202020 !important;
  font-size: 11px;
  color: #E8E8E8;
}

/* guest_access */
#roster div.footer .guest_toggle>div {
  border-top-color: #636363;
  background-color: #202020;
  color: #E8E8E8;
}
/* sidebar -- end */

}
