@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dailymail.co.uk")

{

  /*Facebook app banner*/
   
   .jkl23j4l23  { display: none !important;}

 /*advert banner*/
    .billboard_wrapper.relative {display: none !important;} 


/*text&background*/

body, #ushome, #home, #news, .nav-primary, .gr6ox, .link-gr6ox a, .cleared, .wocc, h3.social-links-title, .pufftext, .masthead, .field, div.form-inner

{
background: #6d6d6d !important;

}

  /*   Facebook share   */ 
  
  
  .share ,.button-share-facebook   {display: none !important;} 
  
  .has-button-share-facebook  {background: #6d6d6d !important; height:100px   !important }
 

         /*   Font size   */
  
        .aileron-font .article-text .mol-para-with-font font, .aileron-font .article-text .mol-para-with-font                    {font-size: 14pt !important;}

        .article .articletext                                                                                                     {font-size: 1pt!important;}


                body, div, dl, dt, dd, ul, ol,  pre, code, form, fieldset, legend, input, textarea, p, blockquote, th, td, a      {font-size: 14pt!important;}


               .p  
                                                                                                                                  {font-size: 14pt!important;}


             h1, h2, h3, h4, h5, h6                                                                                              {font-size: 20pt !important;}

            font, span                                                                                            {color:#000  ; font-size: 14pt  !important;} 

     /*   Font size   */



/*VIDEO CONTROLLS*/

 .vjs-control-bar {visibility:inherit !important;}



/*links&background*/


  h2, #articletitle wogr3    { background-color: #6d6d6d !important}
  
  a, bold    { background-color: #6d6d6d !important}

a ,li, p-119

{
color:#000!important;
  background-color: #6d6d6d;
}


/*DM screen saver advert*/

 .page_banner_overrides,  .page-banner articlePage { background-color: #6d6d6d !important}



/*highlight*/

 

  
  .bogr2, .link-bogr2 a, .linkro-bogr2 a:hover {
    color: #000;
    background-color: #6d6d6d;
}
  
  
  
 div.articletext, span.pufftext, .articletext, .ul, .li, .page, .puff, .alt-femail   { color:#000!important;}
  
 div.linkro-wocc, strong, cleared  {color: #000!important;} 
  

p {font-size: 14pt!important;}
  
font, span   {color:#000  ; font-size: 15pt  !important;} 
  
  
 article-text {color:#fff!important;} 
  
  
  /*   comments   */
  
.dm-tabs, dm-tabs, li  {color:#000!important;}

.comment-body, comment-text
  
  {font-size: 12pt!important;} 


   .rating-button-inline, .gr5ox {font-size: 10pt!important;} 

  
   /*   comments quote   */
 #reader-comments ,.comment-post p ,.comment-post, container, reply-post, container
  
  

  
  
  /*   comment box   */
  
  .article-reader-comments textarea {
    border: 1px solid #8c8c8c;
    padding: 4px 0px 2px;
    
    background: #6d6d6d !important;
    font-size: 12pt!important;
    
}
  
  .buttons-holder { float: right; margin: -14px;}
  
 .topcommenter { padding: 8px 8px 8px !important;
} 
  
}
