css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dailymotion.com"),
domain("dmcdn.net")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, #footer, .container, .search, .foreground, .foreground_hoverable, .foreground a, #content, #header, #header_nav, .content_right, .box_content, div.dmpi_bigbox, body.dm_page_html_video div.video_description, .foreground2, .foreground2 a, .background_3, .background_3_on_hover:hover

, .topwrapper_content, .pdg-top-lg, bodyall, .user-details, mrg-btm-lg, brd-rad-md, js-user-description, row, mrg-top-lg, mrg-btm-lg, label, user-description, .sd_header, .sd_externalwidget_header 

{
color: black !important;
background: #7d7d7d !important;
}

input.dmco_form_text

{
color: #000 !important;
}

  .video_title   {color: #000 !important;}

/*links*/

.no-touch .sd_video_itemrelated:hover .sd_video_title span  {color: #000 !important;}


 title, .link ,  .alt-link

  
{color: #000 !important;}
  
 .div, #login_form, #login_form_username, form_input  check rule_required   {color: #FFF !important;}
  

/*highlight*/

textarea, pre, input.dmco_form_text

{
background: #6d6d6d !important;}

.topwrapper_content, pdg-top-lg, bodyall, user-details, mrg-btm-lg, brd-rad-md, js-user-description, row, mrg-top-lg, mrg-btm-lg, label, user-description



{background: #6d6d6d !important;}
  
  
  /*    upload    */
  
  
.upload {background: #6d6d6d !important;}
  
  
  .upload_status_notif {color: #000 !important;}
  
upload_button, upload_video_button, dmco_simplelink, btn, btn-primary, dmco_link, not_ajax     {background: #fff !important;}
  

}
