css:
/* Yahoo!Japanトップページ */
@-moz-document url(http://www.yahoo.co.jp/){
    /* header */
    #wrapper > #header > #hdBar,
    #wrapper > #header > #emg,
    #wrapper > #header > #masthead > #changeMode > #changeModeOf,
    #wrapper > #header > #masthead > #siteinfo,
    #wrapper > #header > #masthead > #mhicon,
    #wrapper > #header > #emergency
    {display:none !important;}
    #wrapper > #header{
        margin:0px 0px 5px 0px;
        height:70px;
    }
    #wrapper > #header > #masthead{
        width:300px;
        height:50px;
    }
    #wrapper > #header > #masthead h1{
        margin:0px 0px;
        padding:0px 0px;
        width:300px;
    }
    #wrapper > #header > #masthead h1 a {
        margin:0px 0px;
        padding:0px 0px;
        position:absolute; top:0px; left:0px;
    }
    #wrapper > #header > #searchbox {
        position:absolute; top:5px; right:5px;
        border-style:none !important;
        background-color: #FFFFFF !important;
        margin:0px 0px;
        padding:0px 0px;
        width:550px;
        height:55px;
    }
    #wrapper > #header > #searchbox form fieldset > #srchbd{
        margin:0px 0px;
        padding:0px 0px;
        width:550px;
        height:55px;
    }
    #srchbd > #srchAssist {display:none !important;}
    /* contents */
    #wrapper > #contents > #toptxt,
    #wrapper > #contents > #navi > #companybox,
    #wrapper > #contents > #navi > #contentbox > #pickupservice,
    #wrapper > #contents > #division > #main > #commerce,
    #wrapper > #contents > #division > #main > #spotlight,
    #wrapper > #contents > #division > #main > #tct,
    #wrapper > #contents > #division > #main > #event,
    #wrapper > #contents > #division > #main > #eventPromo,
    #wrapper > #contents > #division > #main > #selectionR,
    #wrapper > #contents > #division > #main > #cgmboxR,
    #wrapper > #contents > #division > #main > #video,
    #wrapper > #contents > #division > #main > #realtime,
    #wrapper > #contents > #division > #main > #cb,
    #wrapper > #contents > #division > #main > #bpComposite,
    #wrapper > #contents > #division > #sub > #brandpanelCp,
    #wrapper > #contents > #division > #sub > #brandpanel.bx,
    #wrapper > #contents > #division > #sub > #brandpanel > #YPAbelt,
    #wrapper > #contents > #division > #sub > #facebook,
    #wrapper > #contents > #division > #sub > #facebookCp,
    #wrapper > #contents > #division > #sub > #yjidboxB,
    #wrapper > #contents > #division > #sub > #yjidbox,
    #wrapper > #contents > #division > #sub > #rightbox,
    #wrapper > #contents > #division > #sub > #ultra
    {display:none !important;}
    /*その他*/
    #wrapper > #promoarea,
    #wrapper > #topImpact,
    #YPAbeltchild
    {display:none !important;}
}

/* Yahoo!ゲーム */
@-moz-document url(http://games.yahoo.co.jp/){
    .yj950-1{background:#fff !important;}
    #yj_wallpaper_left,#yj_wallpaper_right,
    #wrapper > #header > #msthd > .msthdlink > .compo,
    #wrapper > #contents > #pilot,
    #wrapper > #contents > .yjGNaviH,
    #wrapper > #contents > #yjGInfo,
    #wrapper > #contents > #yjGMainWrap > #yjGNavi > .yjGBaner,
    #wrapper > #contents > #yjGMainWrap > #yjGCenter > .mgn15b,
    #wrapper > #contents > #yjGMainWrap > #yjGCenter > #yjGMainFocus,
    #wrapper > #contents > #yjGMainWrap > #yjGCenter > .yjGSerch,
    #wrapper > #contents > #yjGMainWrap > #yjGSub > #yjYSP,
    #wrapper > #contents > #yjGMainWrap > #yjGSub > #yjLrec,
    #wrapper > #contents > #yjGMainWrap > #yjGSub > .mgn15b,
    #wrapper > #contents > .mgn15b,
    #wrapper > #contents > #yjGFoot,
    #wrapper > #footer > #uft,
    #wrapper > #contents > #yjGMainWrap > #yjGSub > #yjGSubHis >.yjGcl > li > .flyout
    {display:none !important;}
}
@-moz-document url(http://games.yahoo.co.jp/play/list){
    #wrapper > #header > #msthd > .msthdlink > .compo,
    #wrapper > #contents > #yjGFoot,
    #wrapper > #footer div
    {display:none !important;}
}
@-moz-document url-prefix(http://games.yahoo.co.jp/info),
               url-prefix(http://games.yahoo.co.jp/calendar),
               url-prefix(http://games.yahoo.co.jp/search){
    #wrapper > #header > #msthd > .msthdlink > .compo,
    #wrapper > #contents > #yjContentsBody > #yjMain > .yjMainGrid div iframe,
    #center1,
    #wrapper > #contents > #yjContentsBody > #yjSub > #yjLrec,
    #wrapper > #footer > #bigfooter,
    #wrapper > #footer div,
    #wrapper > #contents > #yjContentsHeader > .CT,
    #wrapper > #contents > #yjContentsBody > #yjMain > .yjMainGrid > #yads_ds63326_16762_01,
    #wrapper > #contents > #yjContentsBody > #yjSub > #yads_ds63326_16762_02,
    #wrapper > #contents > #IM
    {display:none !important;}
}

/* Yahoo!ボックス */
@-moz-document url-prefix(https://box.yahoo.co.jp/user/viewer){
    #wrapper > #header > .pimServiceLinkModule > .clearFix,
    #wrapper > #content > #main-wrapper > #main > .pickupModule,
    #wrapper > .bottomLinkModule,
    #wrapper > #footer
    {display:none !important;}
}
@-moz-document url-prefix(https://box.yahoo.co.jp/guest/){
    .extraInfoModule > div:nth-child(1),
    .pimServiceLinkModule,
    #msthd,
    #sub,
    .bottomLinkModule,
    #footer
    {display:none !important;}
}
