css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gameminer.ru") {
body {
color: rgb(0, 0, 0);
background-color:rgb(218,218,218);
}
.container,.chat__drag {
background-color:rgb(218,218,218);
}
.header {
background-image:url("data:image/jpeg;base64,")
}
.account__menu,.header__menu-item {
color:rgb(0, 0, 0);
}
.g-golden{
color: rgb(255, 193, 15);
}
.giveaway-comments-pages {
    background-color: #C7CBCE;
}
.giveaway-comment-quote__head {
    background-color: #C7CBCE;
}
.footer {
    background-color: #CECECE;
    background-image: -moz-linear-gradient(center top , #C6C6C6, #D9D9D9);   
    background-image: -webkit-linear-gradient(top , #C6C6C6, #D9D9D9);    
}
.toper {
    background-color: #D6D8DB;
    color:#000000;
}
.giveaway-search {
    background-color: #DDE0E1;
}
label {
    color:#000000;   
}
.game-container {
    background-color: #D5D8DA;
}
.dashboard {
    color: #000000;
}
.giveaway {
    background-color: #DDE0E1;
}
.g-white {
    color: #000000;
}
.g-level-icon {
    color: #000000;
}
.g-dropdown {
    background-color: #DDE0E1;    
}

.slots__bet {
    background-color: #999999;
}

.giveaway-info__name {
    color: #999999
}
.giveaway-add-comment h3 {
    color: #999999;   
}    

.active.nav_gray > a, .active.nav_gray > a:hover {
    background-color: #999999;   
}    
.inventory-item {
    background-color: #999999;
}  
.pagination .active a {
    color: #000000;          
}
.pagination a:hover, .pagination .active a {
    background-color: #999999
}
.table tbody tr:hover td, .table tbody tr:hover th {
    background-color: #999999;
}  
.achievement h3, .achievement-view h3 {
    color: #999999;
}    

.topic-info a {
    color: #606060;
}    
.btn-inverse {
    background-color: #d0d0d0; /*  #696969;*/
    /*background-image: -moz-linear-gradient(center top , #757575, #565656);   */
    background-image: -moz-linear-gradient(center top , #dadada, #c1c1c1);
    color: #333333;
}
.btn-inverse:hover {
    background-color: #c1c1c1;
    background-position: 0 -15px;
    color: #333333;
    text-decoration: none;
    transition: background-position 0.1s linear 0s;
}    
    
.messages__message:hover {
    background-color: #999999;
}    

.messages__message-author {
    color: #303030;
}    
.inventory-item-info {
    background-color: #999999;
}
.inventory-item-info__data {
    background-color: rgb(218,218,218);
}
.topic-last-answer__profile {
    color: #606060;
}
.nav-tabs > .active > a, .nav-tabs > .active > a:hover {
    background-color: #B0B0B0;
    border: 1px solid #909090;
}
.nav-tabs > li > a {
    border: 1px solid #909090;
}
.nav-tabs {
    border-bottom: 1px solid #909090;
}
a.event {
    color: #000000;
}    

.search-item__account {
    color: #1ab2ff;
}    

.chat__message-nickname, .profile__name {
    text-shadow:
1px 0px 0 #808080,
1px 1px 0 #808080,
0px 1px 0 #808080; 
}    
}
