css:
@-moz-document domain("atelier801.com") {
a:hover, a:focus {
	color: #3BF;
	text-decoration: none;
}

blockquote {
	border-left-color: #066;
}

.cadre-quote {
	max-height: 15em;
	overflow-y: auto;
}

.cadre-forum-titre {
	font-size: x-large;
	text-shadow: 0 0 1px;
}

.cadre-section-mini:hover {
	background-color: rgba(0, 0, 0, 0.1);
}

.cadre-section-mini .img20 {
	float: left;
}

.cadre-section-titre-mini {
    display: block;
	padding-left: 30px;
}

.cadre-sujet,
.cadre-message {
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    border-bottom: 1px solid rgba(0, 0, 0, 0.8);
}

.cadre-sujet-titre.lien-blanc {
	display: block;
	font-size: 100%;
}

.cadre-sujet-infos {
	display: initial;
	visibility: visible;
}

.cadre-sujet-infos.hidden {
	opacity: 0.4;
}

.bouton-cadre-auteur-mini {
	display: none;
}

.cadre-type-auteur-joueur {
    font-weight: normal;
}

.nombre-messages {
	font-size: 14px;
}
}
