@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www-01.ibm.com/support/knowledgecenter"),
url-prefix("https://www-01.ibm.com/support/knowledgecenter"),
url-prefix("http://www.ibm.com/support/knowledgecenter"),
url-prefix("https://www.ibm.com/support/knowledgecenter") {
    @font-face {
        font-family: 'Roboto';
        font-style: normal;
        font-weight: 300;
        src: local('Roboto Light'), local('Roboto-Light'), url(https://fonts.gstatic.com/s/roboto/v15/Hgo13k-tfSpn0qi1SFdUfVtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
    }
    @font-face {
        font-family: 'Roboto';
        font-style: normal;
        font-weight: 700;
        src: local('Roboto Bold'), local('Roboto-Bold'), url(https://fonts.gstatic.com/s/roboto/v15/d-6IYplOFocCacKzxwXSOFtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
    }
    @font-face {
        font-family: 'Roboto';
        font-style: italic;
        font-weight: 300;
        src: local('Roboto Light Italic'), local('Roboto-LightItalic'), url(https://fonts.gstatic.com/s/roboto/v15/7m8l7TlFO-S3VkhHuR0at44P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
    }
    @font-face {
        font-family: 'Roboto';
        font-style: italic;
        font-weight: 700;
        src: local('Roboto Bold Italic'), local('Roboto-BoldItalic'), url(https://fonts.gstatic.com/s/roboto/v15/t6Nd4cfPRhZP44Q5QAjcC44P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
    }
    @font-face {
        font-family: 'Inconsolata';
        font-style: normal;
        font-weight: 400;
        src: local('Inconsolata Regular'), local('Inconsolata-Regular'), url(https://fonts.gstatic.com/s/inconsolata/v15/BjAYBlHtW3CJxDcjzrnZCI4P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
    }
    body {
        font-family: Roboto, 'Roboto', 'Microsoft YaHei', sans-serif !important;
        font-size: 16px !important;
        line-height: 1.5 !important;
        font-weight: 300 !important;
        word-wrap: break-word;
        word-break: normal;
    }
    p,
    li,
    a,
    strong,
    dd {
        font-family: Roboto, 'Roboto', 'Microsoft YaHei', sans-serif !important;
        font-size: 1.0em !important;
        line-height: 1.5 !important;
        font-weight: 300 !important;
        word-wrap: break-word;
        word-break: normal;
    }
    h1,
    h2,
    h3,
    h4,
    h5,
    dt {
        font-family: Roboto, 'Roboto', 'Microsoft YaHei', sans-serif !important;
        line-height: 1.5 !important;
        font-weight: 700 !important;
    }
    code,
    pre {
        font-family: 'Inconsolata', 'Microsoft YaHei', monospace !important;
        font-size: 1.0em !important;
        line-height: 1.5 !important;
        font-weight: 400 !important;
        word-wrap: break-word;
        word-break: break-all;
    }
    p code,
    p pre,
    li code,
    li pre,
    .option,
    .cmdopt,
    .syscmdopt {
        font-family: 'Inconsolata', 'Microsoft YaHei', monospace !important;
        font-size: 1.0em !important;
        line-height: 1.5 !important;
        font-weight: 400 !important;
        word-wrap: break-word;
        word-break: break-all;
    }
    pre.displaycode {
        font-family: 'Inconsolata', 'Microsoft YaHei', monospace !important;
        font-size: 1.0em !important;
        font-weight: 400 !important;
        line-height: 1.5 !important;
        word-wrap: break-word;
        word-break: break-all;
    }
}
