css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 

domain("ubisoft.com"),
domain("ubi.com") 
{

html, .body-home-container, .body_wrapper, .foruminfo,
.forum_info_subblock, .toolsmenu div, .postrow, .restore,
.threadinfo, .threadstats, .threadlastpost  .userinfo,
.postdetails, .userinfo, .textcontrols, .postbody, .section,
.blockrow, .textbox, input, textarea, .cke_wrapper,
.options_input_block, .forum_info_form, .navlinks, .blockfoot,
.threadlastpost, .nonsticky, .sticky, .quote_container,
.login, #forum_icon_legend dl, .userinfo_noavatar,
.activitybit, .underblock, .floatcontainer,
.profile_widgets, .forumrow, #right-content-area,
.ui-accordion-header, .rn_Multiline2 a, .text-expanded,
.title-expanded, .latest-news li, .table-title,
.q a, .chzn-single, #askquestion-container,
.chzn-results, .table-lines,
#charactersLeft
{
background: #000000 !important;
}
.module-wrap
{
background: #000000 !important;
}
#usercp_nav li a:hover,
#usercp_nav .active ,
.blocksubhead, .faq-container,
#posting_rules, #forumrules, .btn 
{
background: #333333 !important;
}
input, textarea
{
border: 1px #FFFFFF solid !important;
}
input.textbox
{
box-shadow: none !important;
}
p, h1, h2, h3, h4, h5, h6, .title, .label, .time,
.titleline a, .forumdescription, dd, strong, dt,
.threadbit .threadstats li, .postbody, a, .faq-text,
.postcontent, .restore, blockquote, .textbox,
 input, label, textarea, .usertitle, b, .faq-title,
#usercp_nav a, li, .navlinks, .blockrow, .tdate,
.text-expanded, .hq-text, .ask-title, .platform-title,
.ask-question span, .tgame, .tplatform, .tref, .tstatus,
.tsubject, .field-name, .field-value, .createdby,
.createddate, .postcontent span
{
color: #FFFFFF !important;
}
}
