css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul"); 
/*隐藏原有背景*/
#appmenu-button {
    background: none !important;
    border: none !important;
	box-shadow: none !important;
    /*修改大小*/
    min-width: 48px !important;
	width: 48px !important;
}
/*添加新背景*/
#appmenu-button-container {
	border-radius: 35px 35px 0 0 !important;
    border: 1px solid rgba(0,0,0,0.3) !important;
    margin-bottom: -5px;
	background-image: url("chrome://branding/content/icon48.png"), -moz-linear-gradient(rgba(200,200,200,.55), rgba(150,150,150,.55), rgba(70,70,70,.7)) !important;
    background-repeat: no-repeat !important;
}
/*隐藏三角*/
#appmenu-button .button-menu-dropmarker {display: none !important;}
/*隐藏文字*/
#appmenu-button .button-box .box-inherit .button-text {display: none;}
