@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.google.com"), domain("encrypted.google.com") {
    #hplogo > a > img {
        display: block;
        box-sizing: border-box;
        background: url(https://31.media.tumblr.com/bbeb98ab73edf30df1721a9bb5b31f54/tumblr_n74gxyDbag1qhejp9o1_500.gif) no-repeat;
        width: 460px; /* Width of new image */
        height: 250px; /* Height of new image */
        padding-left: 460px; /* Equal to width of new image */
    }
