css:
@-moz-document url-prefix("http://gatherer.wizards.com/Pages/Search/Default.aspx") {
.cardItemTable {
    box-shadow: 0 0 20px #222;
}

.cardItemTable .cardItem.evenItem {
    background-color: rgb(200, 200, 200);
}

.cardItemTable .cardItem.oddItem {
    background-color: rgb(207, 207, 207);
}

.cardItemTable .cardItem {
    color: transparent;
}

.cardItemTable table {
    border-collapse: collapse;
    border-bottom: solid #999 1px;
    margin-bottom: 0px;
}

.cardItemTable table .setVersions {
    border-left: solid #999 1px;
    width: 44px;
}

.cardItemTable .leftCol a {
    margin: 10px 10px 20px 5px;;
    display: block;
    overflow: hidden;
    width: 181px;
    height: 133px;
    box-shadow: 0 0 15px #888888;
}

.cardItemTable .leftCol a img {
    height: 310px;
    width: 223px;
    margin-left: -21px;
    margin-top: -38px;
}

.cardItemTable .cardInfo .cardTitle a {
    font-family: 'Arial Black';
    font-size: 1.1em;
    color: rgb(30, 30, 30);
}

.cardItemTable .cardInfo {
    position: relative;
}

.cardItemTable .cardInfo .manaCost {
    position: absolute;
    right: 8px;
}

.cardItemTable .cardInfo .convertedManaCost {
    display: none;
}

.cardItemTable .cardInfo .typeLine {
    font-weight: normal;
    font-size: 1.1em;
    color: rgb(30, 30, 30);
    padding-top: 0.6em;
    display:  inline-block;
}

.cardItemTable .cardInfo p {
    color: black;
    margin-top: 0.8em;
    margin-bottom: 0.8em;
    padding-bottom: 0;
}

.cardItemTable .cardItem .rightCol {
    vertical-align: middle;
    width: 44px;
}

.cardItemTable .cardItem .rightCol .otherSetSection {
    width: 80%;
    margin: auto;
    margin-top: 3px;
    padding-top: 7px;
    border-top: solid #999 1px;
}

.cardItemTable .cardItem .otherSetSection .rightCol {
    width: auto;
}

.cardItemTable .cardItem .rightCol .otherSetSection > div:first-child {
    display:  none;
}
}
