@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

img[src*="/images/star_1.png"]{
  width: 0 !important;
  height: 30px !important;
  padding-left: 29px !important;
  background-image: url("http://i36.tinypic.com/35b8p4g.jpg") !important;
}
#view-code {
background: #606060 url('http://i36.tinypic.com/192rlk.jpg')repeat-x !important;
}
img[src*="/images/star_0.png"]{
  width: 0 !important;
  height: 30px !important;
  padding-left: 29px !important;
  background-image: url("http://i36.tinypic.com/1dfme.jpg") !important;

}

#navigation a,#navigation .current-page{
-moz-border-radius-topleft:0px !important;
-moz-border-radius-topright:0px !important;
background: url('http://i34.tinypic.com/2uzqkif.jpg')repeat-x !important;
color:white !important;
border-color:#293645 !important;
border-style:solid solid none !important;
border-width:1px 1px medium !important;
}

#navigation a:hover{
-moz-border-radius-topleft:0px !important;
-moz-border-radius-topright:0px !important;
background: url('http://i35.tinypic.com/30c81aa.jpg')repeat-x !important;
color:white !important;
}

#header-bar {
-moz-border-radius-bottomleft:10px !important;
-moz-border-radius-bottomright:10px !important;
-moz-border-radius-topleft:0 !important;
-moz-border-radius-topright:0 !important;
background-color:#426395 !important;
background: url('http://i34.tinypic.com/dh4jtg.jpg')repeat-x !important;
border-top:0 none !important;
height:3.5em !important;
margin-top:0 !important;
padding:5px !important;
border-style: solid !important;
border-width: 1px !important; 
border-bottom-color:#6090c6 !important;
border-left-color: #6090c6 !important;
border-right-color: #6090c6 !important;
border-top-color: #6090c6 !important;
}

p,div,h1,h2,li,th{
color: white !important;}

p{font-size: 80% !important}

#logo a, #logo a:visited {
color:white !important;
text-decoration:none !important;
}

#show-code{
color:white !important;
}

#account-link a {
color:white !important;
}

a:link{color: #828282 !important;}
a:hover{color: #FFFFFF !important;}
a:visited{color: #5a5a5a !important;}

body {
background:#282828 url(http://i36.tinypic.com/f1fnuo.jpg) repeat-x scroll center top !important;
margin:0 !important;
padding:0 30px !important;
}

}
