css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {

body {
  background: url("http://imageshack.com/a/img855/7291/x58j.png") no-repeat fixed top left #ffebeb !important;
   /*255,235,235 = #ffebeb pink pin stripes*/
/*  background-color: rgba(250,111,4, 0.50) !important;/*#fa6f04*/
/*  background-position: 0% 0% !important;*/
  background-size: /*100% 105%*/auto 100% !important;
}
BODY.mceContentBody { background: white !important }

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(255,122,149, 0.50) !important;/*#ff7a94*/
}
.selection_nipple {
  border-bottom-color: rgba(255,122,149, 0.50) !important;
}

#right_column:after {
  background: linear-gradient(to bottom, rgba(255,122,149,0.5), rgba(255,122,149,0)) !important;
}

.search_form_row {
  background-color: rgb(255,188,202) !important;
}
.controls_section .count {
  color: inherit !important;
}

#pagination > A,
#dashboard_controls {
  color: rgb(255,122,149) !important;
}

#pagination > A:hover {
/*  color: rgb(204, 98, 120) !important;*/
  text-shadow: 1px 1px 1px rgb(255, 215, 223) !important;
}

#dashboard_controls A, #dashboard_controls .control {
  color: inherit !important;
  text-shadow: none !important;
  /*text-shadow: 1px 1px 1px rgb(153, 73, 89) !important;*/
  text-shadow: 1px 1px 1px rgb(255, 215, 223) !important;
}

/*ICON BAR BACKGROUND begin - background for the icon bar*/
#-new_post {
   background-color: rgba(255, 255, 255, 0.25) !important;
}

#logo {
	height: 49px !important;
	width: 230px !important;
        top: 100px !important;
}
.logo > .logo_anchor {
	background-image: url('http://img29.imageshack.us/img29/7594/tumblrhw.png') no-repeat fixed top left 
!important;
}
#return_to_top .return_to_top_icon{
  /*background: url('http://i1311.photobucket.com/albums/s669/porknoodles/mikan06_zps48261785.png') no-repeat transparent!important*/
}

#popover_blogs .tab_notice:after{display:none; }

div.l-header .tab.iconic > a {
	background-image:url('https://38.media.tumblr.com/0a20f429bee3dcc550b93e8f5e648248/tumblr_n79h5nfyWs1r9w2f5o1_r1_250.png') !important;
}

.new_post_label {
  font-size: 12px !important;
  color: rgb(255,122,149) !important;
  text-transform: uppercase;
  padding-top: 69px !important;
  padding-bottom: 10px !important;
  transition-property: padding !important;
}
.new_post_label:hover {
  padding-top: 64px !important;
  padding-bottom: 15px !important;
}

.new_post_label > I:before { content: none !important }

.new_post {
	background: #ffffff url('data:image/png;base64,') no-repeat !important;
	background-size: 100% auto !important;
}
.new_post.editing {
	background: linear-gradient(to right, rgb(253,253,253) 50%, rgb(254, 235, 235) 50%) fixed repeat-x !important;
	background-size: 30px auto !important;
}
.plexi.show.color {
  background-color: rgba(255, 122, 149, .85) !important;
}

.controls_section li .posts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.posts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .posts:before {
  content: "Posts" !important;
}.controls_section li .followers:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.followers  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .followers:before {
  content: "Nerds" !important;
}.controls_section li .drafts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.drafts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .drafts:before {
  content: "Drafts" !important;
}.controls_section li .queue:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.queue  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .queue:before {
  content: "Queue" !important;
}


}
