css:
@-moz-document domain("nolinks.net") {
#wrapheader, .logodiv, .navbar-links li a, .panel-header, .panel-header .panel-icon, li.header, li.header .panel-icon, table.table1 thead th {
    	background-color: #1F1F1F !important;
    	opacity: 1 !important;
    }
    
    body {
	   color: #333333;
    }

    .panel, .panel a, .sblock, .sidebar-inside-sb, #forum-menu .btn-navbar, #search .adv-search-btn, #search .icon-plus-circled, #sidebar input, #search .button2, .row, .cp-mini, .post {
    	background-color: #3F3F3F !important;
    	color: #C5C5C5;
    }

    li.row, #cp-main .panel li.row { 
    	border-bottom: 1px solid #333333 !important;
    	border-top: 1px solid #333333 !important;
    }

    #navigation a, #tabs a span, input.button1, input.button2, input.button3, .inputbox, select, hr, .buttons a, .inside-search-box, .search-box .inside-search-box .button2, .pagination span a, .pagination span strong, table.table1 tbody td, table.table1 tbody td a, .row .pagination span a { 
    	background-color: #525252 !important;
    	border-color: #333333 !important;
    	color: #C5C5C5 !important;
    }

    li.row:hover, #navigation a:hover, #tabs a:hover span, input.button1:hover, input.button2:hover, input.button3:hover, .buttons a:hover, .search-box .inside-search-box .button2:hover, .row .pagination span a:hover, .pagination span a:hover {
    	background-color: #767676 !important;
    	border-color: #333333 !important;
    	color: #C5C5C5 !important;
    }

    dl.mini dt, .panel .details dd, label, dd label, li.row dt a, #message-box textarea, .post:target .content, .content, .content a, .inner a, .notice, .postprofile dd strong, .signature, h3, .postlink, .postlink:visited, ul.topiclist li, .search .postprofile a , .search .postprofile a:hover {
    	color: #C5C5C5 !important;
    }

    .postprofile dt, .postprofile dd, .spoiler-box {
    	background-color: #3E3E3E !important;
    	border-color: #333333 !important;
    	color: #C5C5C5 !important;
    }

    postbody ul.profile-icons li a:hover, .postlink:hover {
    	background-color: transparent;
    	color: #767676 !important;
    }

    textarea, blockquote, blockquote blockquote, blockquote blockquote blockquote, blockquote.uncited { 
    	background-color: #525252 !important;
    	border-color: #333333 !important;
    	color: #C5C5C5 !important;
    }
    
    a[href$="=172"], a[href$="=497"], a[href$="=1154"], a[href$="=1547"], a[href$="=3364"] {
        color: #FF0000 !important;
    }

	a[href$="=130"], a[href$="=272"], a[href$="=345"], a[href$="=1263"], a[href$="=3170"], a[href$="6543"], a[href$="9961"] {
		color: #00AA00 !important;
	}
}
