css:
@-moz-document url-prefix(http://crusadersscape.com/forums/) {

body{background: #171320 url('http://i771.photobucket.com/albums/xx352/Jeantix/dempurple.png') !important;}

.ipsSideBlock h3 {
background: #170629 !important;
line-height: 27px !important;
border-bottom: 5px solid #0B0311 !important;
border-top-left-radius: 5px !important;
border-top-right-radius: 5px !important;
}
.ipsSideBlock {
background: #441554 !important;
border: 1px solid #581279 !important;
border-radius: 5px !important;
}

#header_bar {
background: #141414!important;
border-bottom: 4px solid #0F0F0F !important;
}

#branding {
background: #171320 url('http://i771.photobucket.com/albums/xx352/Jeantix/tile.png') !important;
height: 100px !important;
line-height: 100px !important;
border-bottom: 0 !important;
}

#search { margin: 37px 0 !important; }

#user_link:hover, #notify_link:hover, #inbox_link:hover {
background-color: #252525 !important;
}

.ipsHeaderMenu { background: #1D1D1D !important;}



#user_link.menu_active {
background-color: #1D1D1D !important;
color: #FFF !important;
}


#primary_nav {
background: #36114C url(http://crusadersscape.com/forums/public/style_images/master/csf_maintitle.png) !important;
line-height: 8px !important;
border-bottom: 3px solid #1D0720 !important;
padding-bottom: 4px !important;
}



#community_app_menu>li>a {
background: #1B0A2E !important;
color: #FFF !important;
padding: 8px 15px 8px !important;
border: 1px solid #5F1283 !important;
border-radius: 4px !important;
}

#community_app_menu>li>a:hover{background:#201938 !important;}

#quickNavLaunch { display: none !important; }


#content {
background: #170B22 !important;
border-left: 1px solid #562781 !important;
border-right: 1px solid #562781 !important;
}


.author_info{
background: #180725 !important;
padding-bottom: 30px !important;
border-bottom-right-radius: 20px !important;
border-right: 1px solid #4D1688 !important;
border-bottom: 1px solid #4D1688 !important;
border-left: 1px solid #4D1688 !important;
margin-left: -1px !important;
} 
  
.signature {
border-top: 1px dashed #2B2B2B !important;
}
.avatar .ipsUserPhoto {
padding: 1px !important;
border: 1px solid #6F02FA !important;
background: #1D161F !important; 
}
 
.maintitle {
border-left: none !important;
border-right: none !important;
border-bottom: 1px solid #270836 !important;
}
    
.ipsBox{ 
  padding: 1px !important;
 background: #1B081B !important;
}
    
.ipsBox_container {
background: #121213 !important;
border: 1px solid #353435 !important;
}
  
    
.post_block {
background: #141414 !important;
border-bottom: 1px solid #181818 !important;
}
    
.post_block h3 {
background: #212121 url(http://crusadersscape.com/forums/public/style_images/master/topic_button.png) repeat-x top !important;
}
      
      
.row2 .author {
display: block;
background: #2B0F38;
background: -moz-linear-gradient(top, #811dbb 0%, #3f0c63 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#811DBB), color-stop(100%,#3F0C63));
width: 175px;
margin-left: -10px;
height: 35px;
border-bottom: 1px solid #3B0F49;
border-right: 1px solid #551369;
text-align: left;
text-indent: 10px;
text-shadow: -1px 1px 0 #1C161D;
  
}      


p.citation{border: 1px solid #5A178F !important;}

blockquote.ipsBlockquote{
border-left: 1px solid #353535 !important;
border-right: 1px solid #353535 !important;
border-bottom: 1px solid #353535 !important;
border-top: none !important;
background: #222 !important;
margin-bottom: 7px !important;
}

.post_body {
background: #1A1A1A  !important;
margin: 0 0 0 177px !important;
padding: 12px !important;
}



a[href="http://crusadersscape.com/forums"]{
margin-top: 2px;
display: inline-block;
height: 85px;
width: 500px;
background: url('http://i771.photobucket.com/albums/xx352/Jeantix/cruzaders.png');
}
a[href="http://crusadersscape.com/forums"] img{
visibility:hidden !important;
}


#footer_utilities{
background: #10051D;
border: 1px solid #271C4D;
border-top: 1px solid #1A0A36;
border-bottom-left-radius: 10px;
border-bottom-right-radius: 10px;
}


#Copyright{
font-size: 10px;
margin-top: -10px;
}

#shouts-global-resizer, #shouts-resizer{
background: #272727 !important;
border-bottom: 1px solid #000 !important;
border-top: 1px solid #333 !important;
}

#shoutbox-shouts-table .row2 td{
background: #0E0E0E !important;
border-bottom: 1px solid #090909 !important;
border-top: 1px solid #999 !important;
color: #FFF !important;
}

#shoutbox-shouts-table {
border: 1px solid #000 !important;
}


.row1, .post_block.row1 {
background: #141414 !important;
}

.row2, .post_block.row2 {
background-color: #0D0D0E !important;
}

.unread {
background-color: #1A1A1A !important;
}


td .input_text{
border: 1px solid #272727 !important;
background: #030303 !important;
color: #FFF !important;
min-height: 100px !important;
max-height: 340px !important;
min-width: 250px !important;
outline: 0 !important;
max-width: 250px !important;
width: 250px !important;
}
  
  .input_text:focus{
border: 1px dashed #9A1BB2 !important;
}


#board_stats .value {
background: #1E0E25 !important;
border: 1px solid #31172D !important;
color: #571E6D !important;
}
  
.input_submit.alt{
background: #212121 url(http://crusadersscape.com/forums/public/style_images/master/topic_button.png) repeat-x top !important;
border: 1px solid #1A1A1A !important;
color: #777 !important;
box-shadow:0 !important;
-webkit-box-shadow:0 !important;
-moz-box-shadow:0 !important;  
}


#search .submit_input {
background: #810DA6 url(http://crusadersscape.com/forums/public/style_images/master/search_icon.png) no-repeat 50% !important;  
border: 1px solid #940DA6 !important;  
}



.statistics {
background: #1D0A30 !important;
padding: 10px !important;
border: 1px solid #380E50 !important;
border-radius: 10px !important;
}


.statistics_head {
display: block !important;
background: #130716 !important;
margin: -10px -10px 5px -10px !important;
line-height: 34px !important;
text-indent: 9px !important;
color: #473461 !important;
}

#topic_active_users .statistics_brief {
color: #030303;
text-shadow: 0 1px 0 #441344;
display: block;
background: #270535;
margin: -10px -10px -13px -10px !important;
line-height: 19px !important;
text-indent: 8px !important;
border-bottom: 1px solid #170920;
border-top: 1px solid #491C5A;
}

}
