@-moz-document domain("askville.amazon.com") {
/* ==== askville ==== */

/* remove gradients */
.body, .body_https { background: none }
.recent, .top-level-topics h4, .left_menu_header { background: #ebebeb; }
.stats_inner, .mpc_stats_inner, .left_menu_body { background: #71aad3; }
/* nice colouring of links */
.footer a:link, .footer a:visited, .footer a:hover, .footer a:active { color: #ffffff!important; }
/* user profile */
.reflected { -webkit-box-reflect: initial; }
.stats_value { text-shadow: 0 0 0; }
#right-column, .stats_outer, .mpc_stats_inner { 
  box-shadow: 0 0 0;
  -webkit-box-shadow: 0 0 0;
  border-color: transparent;
}
#right-column { background-color: #ebebeb }

}
