/* defaults for all links */
a:link, a:visited, .link { color: #fff !important; }
a:active, .link:active { color: #000000 !important; }
a:hover { color: #00a5a5!important; }

a:link {
        font-weight: bold !important;
        text-decoration: none !important;
}

/* disable dashed outline on focused elements */
:focus { outline: none; }

/* Main text color */
div, td, font[color="#545454"] { color: #a5a5a5!important; }

/* Text link color */
.a, .g font[color="#fff"] { color: #fff !important; }

#title {
        background-image: url('http://i38.photobucket.com/albums/e128/blue21bucket/ReleaseLoglogoRomeFinal.jpg') !important;
        background-repeat: no-repeat !important;
        height: 102px !important;
        border: 0px solid #000 !important;
        background-position: top !important;
       padding-left:300px !important; }

#wrap {
	padding-left:12px !important;
	margin-right: auto !important;
	margin-left: auto !important;
	margin-top: 10px !important;
	background-color: #000 !important;
	background-position: center !important;
	border: 11px solid #000 !important;
        width:95%!important;}

#content {
	float: left !important;
	width: 1500px !important;
	padding-bottom:20px !important;
        background-color: #000 !important;
        margin-left: 10px !important;
        font-size: 1.13em !important;}

#sidebar {
	float: right !important;
	display: inline !important;
	padding:15px !important;}

#sidebar ul, #sidebar ul ol {
	margin: 0px !important;
	padding-top: 15px !important;
        background-color: #111 !important;}
    
#sidebar ul li ul li ul.children li {
        margin-left:5px !important;}


#sidebar h2{
	font-size: 1.8em !important;
	text-transform: uppercase !important;
	color: #00a5a5 !important;
	font-weight: bold !important;
	padding-top: 0px !important;
	margin: 0 !important;}

#sidebar ul ul ul, #sidebar ul ol {
	margin: 0 0 0 5px !important;}

#sidebar ul ul, #sidebar ul ol {
	margin: 0px 0 0 23px !important;}

.entry {width: 1050px !important}

h3.entrytitle a{
	border:1px !important;
	font-weight: normal !important;
        font-size:1.2em !important;}

img[src*="files/header_logo.jpg"] {
        opacity: .0 !important;
        height: 64px !important;
        width: 600px !important;
}


