css:
@-moz-document domain("atelier801.com") {
/*Link Tags*/

a {
color: #009D9D;
text-decoration: underline;
}

a:hover, a:focus {
color: #2ECF73;
}

.dropdown-menu>li>a {
display: block;
padding: 3px 20px;
clear: both;
font-weight: normal;
line-height: 20px;
white-space: nowrap;
text-decoration: none;
}
        
.nav ul a {
color: #c2c2da;
text-decoration: none;
}

/*Thread parent dropdown*/

.dd-selected {
overflow: hidden;
display: block;
padding: 5px;
font-weight: bold;
color: #08c;
text-decoration: none;
}

/*Cadres*/

.cadre {
min-height: 10px;
padding: 2px 5px;
-webkit-border-radius: 9px;
-moz-border-radius: 9px;
border-radius: 9px;
color: #c2c2da;
overflow-wrap: break-word;
}

.table-cadre-cellule-principale {
width: 100%;
max-width: 1px;
font-family: "Verdana";
font-size: 14px;
}

.cadre-forum {
background-color: #254558;
padding-top: 5px;
margin-bottom: -10px;
height: 100%;
}

.cadre-section {
background-color: #274850;
vertical-align: middle;
margin-bottom: -10px;
height: 100%;
min-height: 32px;
}

.cadre-sections {
padding-bottom: 0;
margin-top: 10px;
margin-bottom: 10px;
background-color: #1a3e4d;
}

.cadre-section-mini {
vertical-align: middle;
margin-bottom: 5px;
display: block;
margin-right: 10px;
}
  
.cadre-section-mini:hover {
color: #fff;
text-decoration: none;
}

.cadre-section-titre-mini {
font-size: 16px;
}

.cadre-forum-titre {
font-size: xx-large;
}

.cadre-type-auteur-joueur {
font-weight: bold;
color: #009d9d;
text-shadow: none;
text-decoration: none;
}
 
.cadre-sujet {
min-height: 30px;
background-color: #1C3C41;
vertical-align: middle;
margin-bottom: 10px;
overflow: visible;
border-top: 1px solid rgba(255, 255, 255, 0.1);
border-bottom: 1px solid rgba(0, 0, 0, 0.8);
}
 
.cadre-message {
border-top: 1px solid rgba(255, 255, 255, 0.1);
border-bottom: 1px solid rgba(0, 0, 0, 0.8);
}
 
.cadre-sujet-titre.lien-blanc {
display: block;
font-size: 100%;
}

.cadre-sujet-postit {
background-color: #18243A;
}
 
.cadre-sujet-infos {
display: initial;
visibility: visible;
}
 
.cadre-sujet-infos.hidden {
opacity: 0.5;
}

.cadre-presentation a {
background-color: #122025;
text-decoration: none;
}

.cadre-quote {
max-height: 15em;
overflow-y: auto;
background-color: #1C3C41;
color: #6C77C1;
font-family: "Verdana";
font-size: 11px;
line-height: 14px;
margin:0px;
}

.cadre-quote small {
color: #009d9d;
font-family: "Verdana";
font-size: 11px;
}

.cadre-utilisateur {
background-color: #2f363f;
margin-bottom: 10px;
font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
text-decoration: none;
font-size: 15px;
}

.cadre-utilisateur-principal {
padding-top: 5px;
word-wrap: break-word;
font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
text-decoration: none;
font-size: 15px;
}

.cadre-tribu {
background-color: #264040;
margin-bottom: 10px;
font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
}

.cadre-tribu-principal {
padding-top: 5px;
word-wrap: break-word;
font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
font-size: 15px;
}

.cadre-tribu-nom:hover {
font-size: xx-large;
text-decoration: none;
}

/*Buttons*/
/*The Hated Avatars*/
.bouton-cadre-auteur-mini {
display: none;
}
/******************/

.btn {
text-decoration: none;
font-size: 14px;
line-height: 20px;
}

.close {
text-decoration: none;
}

.nav-tabs>li>a {
padding-top: 8px;
padding-bottom: 8px;
line-height: 20px;
border: 1px solid transparent;
-webkit-border-radius: 4px 4px 0 0;
-moz-border-radius: 4px 4px 0 0;
border-radius: 4px 4px 0 0;
  text-decoration: none;
}
  
.element-bouton-profil {
display: block;
text-align: left;
font-weight: bold;
color: 009D9D
}
/*Liens (Text in French)*/
.lien-blanc {
color: #c2c2da;
text-decoration: none;
}

.lien-blanc:hover, .lien-blanc:active, .lien-blanc.active, .lien-blanc.disabled, .lien-blanc[disabled] {
color: #fff;
text-decoration: none;
}

.lien-date {
color: #6c77c1;
}

.lien-date:hover {
color: #8e99f0;
text-decoration: none;
}

/*Main Page*/           
.texte-jeu {
text-align: justify;
font-size: medium;
padding-bottom: 3px;
}
/**********/
 
/*Images Misc*/

img {
border: none;
margin: 0px 0px 0px 0px;
-khtml-border-radius: 5px;
-webkit-border-radius: 5px;
border-radius: 5px;
width: auto\9;
height: auto;
vertical-align: middle;
border: 0;
-ms-interpolation-mode: bicubic;
}
 
.img10 {
display: none;
}

.img16 {
width: 16px;
padding: 0px;
border: none;
-moz-border-radius: 0px;
-khtml-border-radius: 0px;
-webkit-border-radius: 0px;
border-radius: 0px;
}

.img100 {
width: 100px;
padding: 0px;
border: none;
-moz-border-radius: 0px;
-khtml-border-radius: 0px;
-webkit-border-radius: 0px;
border-radius: 0px;
}
 
.img20 {
width: 20px;
padding: 0px;
border: none;
-moz-border-radius: 0px;
-khtml-border-radius: 0px;
-webkit-border-radius: 0px;
border-radius: 0px;
}
 
.img32 {
width: 32px;
padding: 0px;
border: none;
-moz-border-radius: 0px;
-khtml-border-radius: 0px;
-webkit-border-radius: 0px;
border-radius: 0px;
}

/*Misc*/

blockquote {
border-left-color: #1C3C41;
}

blockquote small:before {
content: '\2014 \00A0';
display: none;
}

.nombre-messages {
font-size: 14px;
}

.navbar-inverse .brand {
color: #999;
text-decoration: none;
}
}
