css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
domain("tecnico.ulisboa.pt") {
    
    /* Flat bar */
    #ist-bar-container,
    #bar-ist-network div#bar,
    #bar-ist-network div#bar-open {
        background: #555;
        box-shadow: none;
    }

    .gsst_a {
        padding-top: 5px; /* Align search box cross */
    }

    .gsc-input .gsc-input-box {
        height: 30px;
    }
}

@-moz-document
domain("fenix.tecnico.ulisboa.pt") {

    /*** BUTTONS ***/
    
    .btn {
        min-width: 40px;
        margin:    0px 4px;
        padding:   2px 8px;
    }
    
    .btn.active {
        color:      #FFF;
        background: #808080;
        
        border: 1px solid #686868;
    }
    
    /*** ANNOUNCEMENTS ***/
     
    #content-block {
        border: 1px solid #E5E5E5;
    }
    
    div.panel-body:not(.clearfix) * {
        font-family: sans-serif !important;
        font-weight: 400;
    }

    /*** TABLES ***/

    h4.mtop025 {
        padding-left: 6px;
    }

    .tstyle1 th,
    .tstyle1 td {
        border-color: #E0E0E0;
        border-left-style:  none;
        border-right-style: none;
        
        font-size: 14px;
    }
    
    .smalltxt {
        font-size: 0.8em;
    }

    /*** MENUS ***/

    h4.mtop025,
    li.navheader strong,
    .panel-title strong {
        font-size:   18px;
        font-weight: 400;
    }
    
    nav#context .nav li.navheader {
        padding-bottom: 5px;
    }

    .nav-pills > li.menuItem > a.item,
    nav#context .nav > li > a {
        border: 1px solid transparent;
        border-radius: 0px;
    }
    
    .nav > li.open > a {
        background: linear-gradient(to right,
            #009EE2 0%, #009EE2 2%, #F8F8F8 2%, #F8F8F8 2%);
        
        border: 1px solid #F8F8F8;
        
        font-weight: bold;
    }
    
    .nav > li.open > a:hover,
    .nav > li.open > a:focus {
        background: linear-gradient(to right,
            #009EE2 0%, #009EE2 2%, #E0E0E0 2%, #E0E0E0 2%);
        
        border: 1px solid #CCC;
    }
    
    .nav-pills > li.menuItem > a.item:hover,
    .nav-pills > li.menuItem > a.item:active,
    nav#context .nav li > a:hover,
    nav#context .nav li > a:active {
        background: #E0E0E0;
        
        border: 1px solid #CCC;
    }
    
    .nav > li.active > a.item,
    .nav > li.active > a.item:hover,
    .nav > li.active > a.item:focus {
        color: #444;
        
        border: 1px solid #DDD;
        border-left-color: transparent;
        
        margin-left: 0px;
    }
    
    .rsslink {
        border: 1px solid transparent !important; /* Fix .nav li > a conflict */
    }

    /*** TABS ***/
    
    header.clearfix {
        border-bottom: none; /* Remove gap on top of main content */
    }

    header .nav > li > a {
        color: #A0A0A0;
        background-color: transparent;

        border: 0px solid transparent;

        margin-right: 8px;
        padding:      6px 12px 10px 12px;
        
        text-transform: uppercase;
        font-weight: bold;
    }

    header .nav > li > a:hover,
    header .nav > li > a:focus {
        color: #000;
        background-color: transparent;
        
        border: 0px solid #CCC;
        border-bottom-width: 4px;
        
        transition: 100ms;
    }

    header .nav > li.active > a,
    header .nav > li.active > a:hover,
    header .nav > li.active > a:focus {
        color: #000;
        background-color: transparent;
        
        border: 0px solid #07A;
        border-bottom-width: 4px;
    }

    /*** FOOTER ***/

    footer {
        padding: 17px 0px 15px 0px; /* Optical alignment */
        
        text-align: right;
    }
    
    footer a {
        color: #333;
    }
    
    footer p.col-sm-10 {
        margin: 0px;
    }

    footer img {
        display: none;
    }
}
