css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nolinks.net") {
    /* full domain - background */
    html, body {
        background-color: #CFCFCF;
    }
    
    /* full domain - admin usernames */
    a[href$="=172"], a[href$="=497"], a[href$="=1154"], a[href$="=1547"], a[href$="=3364"] {
        color: #FF0000 !important;
    }

    /* full domain - mod usernames */
	a[href$="=130"], a[href$="=272"], a[href$="=345"], a[href$="=1263"], a[href$="=3170"], a[href$="6543"], a[href$="9961"] {
		color: #00AA00 !important;
	}
    
    /*******************************************************************************************************************/
    
    /* index.php - headers */
    #forum-menu .btn-navbar, #sidebar h4, #wrapheader, .header, .headerbar, .logodiv, .navbar-links li a, .panel-header, .panel-icon, .sheader, table.table1 thead th, #sidebar h2.panel-header a, li.header dt{
        background-color: #4B73AA;
        color: #FFFFFF;
        font-weight: normal;
        opacity: 1;
    }
    
    /* index.php - searchbar */
    #search i, #sidebar input, .search-block-content, .sidebar-inside-sb, .adv-search-btn, .icon-plus-circled, .sidebar-inside-sb input.inputbox {
        background-color: #4B73AA !important;
        color: #FFFFFF;
    }
    
    /* index.php - sidebar */
    #sidebar .scontent, #sidebar div a {
        background-color: #CFCFCF;
        color: #000000;
    }
    
    #sidebar ul li a:hover {
        background-color: #CFCFCF;
        color: #000000;
        text-decoration: underline;
    }
    
    /* index.php - forums */
    a.forumtitle {
        color: #000000;
        font-weight: normal;
    }
    
    /*******************************************************************************************************************/
    
    /* viewforum.php - topics */
    a.topictitle {
        color: #000000;
        font-weight: normal;
    }
    
    a.topictitle:visited {
        color: #000099;
    }
    
    li.row {
        background-color: #DDE3EB;
        border-bottom: 1px solid #CFCFCF;
        border-top: 1px solid #CFCFCF;
    }
    
    .pagination span strong, .pagination span a:hover {
        background-color: #DDE3EB;
        border-color: #CFCFCF;
        color: #000000;
    }
    
    .row .pagination span a:hover {
        background-color: #DDE3EB;
        border-color: #6896D5;
        color: #000000;
    }
    
    /*******************************************************************************************************************/
    
    /* viewtopic.php - polls */
    form .panel {
        background-color: #CAD2DB;
        border: 2px solid #6896D5;
    }
    
    form .panel h2 {
        border-bottom-color: #4B73AA;
        color: #000000;
    }
    
    fieldset.polls dl {
        border-top-color: #6896D5;
        color: #000000;
    }
    
    dt label {
        color: #000000;
    }
    
    .pollbar1, .pollbar2, .pollbar3, .pollbar4, .pollbar5 {
        background-color: #4B73AA;
        border-bottom-color: #4B73AA;
        border-right-color: #4B73AA;
    }
    
    /* viewtopic.php - posts */
    #content {
        color: #CFCFCF;
    }
    
    .post {
        background-color: #CAD2DB;
        border-top: 15px solid #6896D5;
        color: #000000;
    }
    
    .content a.postlink, a:link {
        border-bottom-color: #000000;
        color: #000000;
    }
    
    .content a.postlink:visited, a:visited {
        border-bottom-color: #000099;
        color: #000099;
    }
    
    /* viewtopic.php - quotes */
    blockquote, blockquote blockquote, blockquote blockquote blockquote {
        background-color: #CAD2DB;
        border: 0px;
    }
    
    blockquote div, blockquote blockquote div, blockquote blockquote blockquote div{
        background-color: #CAD2DB;
        border: 2px solid #6896D5;
    }
    
    cite {
        background-color: #6896D5;
    }
    
    /* viewtopic.php - spoilers */
    .spoiler-box {
        background-color: #CAD2DB;
    }
    
    /* viewtopic.php - post reply/quick reply/who is online */
    #postform .panel, #options-panel, #postingbox, .panel {
        background-color: #CFCFCF;
        border: 0px;
    }
    
    .content {
        color: #000000;
    }
    
    .signature {
        border-top-color: #000000;
        color: #000000;
    }
    
    /* viewtopic.php - profile */
    .postprofile {
        border-right: 1px solid #6896D5;
    }
    
    .postprofile dt, .postprofile dd {
        background-color: #CAD2DB;
    }
    
    .postprofile dd a:link, .postprofile dd a:visited {
        color: #000000;
    }
    
    /*******************************************************************************************************************/
    
    /* posting.php */
    #topicreview {
        background-color: #CFCFCF;
    }
    
    .postbody h3 a {
        color: #000000;
        font-weight: normal;
    }
}
