css:
.yucs-fl-left 	{ 	display:none !important; }
.yucs-fl-right 	{ 	display:block !important; Background-color:transparent !important; border:0px solid #a3bbdd !important;
  					padding-top:-3px !important; padding-bottom:-3px !important; padding-right:0px !important; padding-left:0px !important; position: absolute !important; z-index:99999999999999999 !important;
  					top:2px !important; Right:5px !important; border-radius:10px !important;}
#yucs { height:10px !important; position:absolute !important; min-width:97% !important;}
#main { top:36px !important; }
#yucs-top-bar { background-color:transparent !important; width:60% !important; border-radius:0px 20px 20px 0px !important; top:3px !important;}
#yucs-top-list { background-color:none !important; display:block !important; width:90% !important; }
#yucs-top-bar #yucs-more-link { border-left: 0px solid #2d1152 !important; }
#yucs-profile_text { display:none !important; }
.yucs-profile {margin-top:0px !important; }
#yucs #yucs-help_button { width:20px !important; }
.yucs-avatar {border-radius:5px !important;}
#yucs-network_link { display:none !important;}
.yucs-fl-right { position:absolute !important; top:0px !important; display:block !important; }

#yucs-profile_inner, #yucs-help_inner { top:30px !important; }

/* i really want this to be global */
#shellcontent{
right:0!important
}

#theAd, #theMNWAd, #slot_MON, #slot_LREC, #slot_MIP, #slot_MON_sent, .darla, .ch_mod, .gxBoom, .boom, #trendmastr-main, #masSearchAd, #slot_MB, #slot_mbrec, .mb-msg-read-img-ad {
display:none!important;
visibility:hidden!important;
}

.panescroll #toolbar {
right:0 !important;
}

.pc.panescroll #shellnavigation, .pc.panescroll #theAd, .pc.panescroll #shellcontent {
top: 0px !important;
}
    
#main, #yucs, #yuhead-bucket {
max-width: 2560px !important;
}

