css:
/*@import url("http://necolas.github.io/normalize.css/3.0.1/normalize.css");*/

p {
  font-size: 24px;
  font-family: 'Georgia Pro', georgia, serif;
  line-height: 1.5em;
}
table[width="470"] p {
  display: inline;
}
p:empty {
  display:block;
}
font[size="5"]{
  font-size:36px;
  font-weight:bold;
}
table center {
  text-align: left;
}
p > a > img,
center > img {
  display: block;
  min-width: 200px;
  height: auto;
  margin: 0 auto 10px;
}
table[width="470"] b {
  font-weight: normal;
}
table {
  width: 100%;
  max-width: 60em;
}
a[target="_top"] > img {
  padding: 20px;
  width: auto;
  min-width: 0;
}
pre {
  height: 1px;
}
