css:
@-moz-document url-prefix("http://huaban.com/") {
/*
Author: DAN;
Website: armgod.com;
*/

#nav_user .group a i {
background: url('http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/icon_user_menu.svg') 0 0 no-repeat;
}

#huaban {
background: url('http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/logo.svg') 0 10px no-repeat;
}
#huaban img{
opacity:0;
}

#sideshare-sns-icon i {
background-image: url('http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/share_icon.svg') !important;
}

#header_main_menu .main-link {
background-image: url('http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/menu_sprite.svg') !important;
}

#header_side_menu .nav i {
background-image: url('http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/menu_sprite.svg') !important;
}

#zoomr_show .zoomin {
background: url('http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/zoom_in.svg') 0 0 no-repeat !important;
}

.pin-view .tool-bar .zoomin i {
background: url('http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/zoom_in.svg') -2px -2px no-repeat;

}

#menu_bar .nav .nav-icon {
background-image: url('http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/menu_sprite.svg');
}

#menu_bar .menu-nav .nav-icon {
background: url('http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/mask_menu_icon.svg') 0 0 no-repeat #999;
}

/*detailPage*/
.huaban-share-unit .share-btns .share-btn,
.huaban-share-unit .menu a i {
background-image: url('http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/share_icon.svg');
}
.btn i.heart,
.pin-view .tool-bar .liked .heart{
background-image: url("http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/action_like.svg") !important;
}
.pin-view .tool-bar-bottom .repin-btn i,
.pin-view .tool-bar-bottom .like-btn i,
.pin-view .tool-bar-bottom .comment-btn i,
.pin .stats>span i{
background-image: url("http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/bt_action_icons.svg");
}
.pin-view .tool-bar-bottom .report-btn i {
background-image: url("http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/report_icon.svg");
}
.pin-view .tool-bar-bottom .source i {
background: url("http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/website.svg") 0 0 no-repeat;
}

/*profilePage*/
#user_card .introduction li i {
background: url("http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/user_info_icons.svg") no-repeat;
}
#user_card .bindings a {
background: url("http://hbdesign-demo.b0.upaiyun.com/140120_retinaHuaban/images/profile_social.svg") 0 0 no-repeat;
}
}
