css:
@-moz-document domain("www.readmore.de") {

body {
color:#B9B9B9;
background: #101010 url('http://abload.de/img/themekjjvm.jpg') no-repeat;
background-position:top 220px center;
background-attachment:fixed;
}

#header {
background: url('http://abload.de/img/head_black_loop51j4t.jpg');
}

#header>div {
background: url('http://abload.de/img/head_blackhakxk.jpg') top center no-repeat;
}

#header li.ucp ul {
background: #414141;
}

#container {
background: #101010;
}

#container>div {
border: 1px solid #101010;
}

.ge_statement.open {
background: #494949;
}

#c_left {
background: #101010;
}

#c_left hr {
background: #101010;
border-bottom: 2px solid #1D1A13;
}

#c_left ul li {
border-bottom: 1px dotted #1D1A13;
color: #B9B9B9;
}

#c_left ul li a {
color: #B9B9B9;
}

#c_left ul.matches_streams li span.title {
color: #B9B9B9;
}

#c_content {
background: #101010;
}

#c_content hr {
background: #1D1A13;
}

#c_right {
background: #101010;
}

input[type=text], input[type=password], textarea, select {
border: 1px solid #1D1A13;
background: #414141;
color: #B9B9B9;
}

#c_right .headlines_cat {
color: #636363;
background: #1D1A13;
}

#c_right .headlines_cat span.icon {
border-left: 1px solid #101010;
opacity: .4;
}

#c_right ul li {
border-bottom: 1px dotted #1D1A13;
color: #B9B9B9;
}

#c_right ul li a {
color: #B9B9B9;
}

#c_right hr {
background: #101010;
border-bottom: 2px solid #1D1A13;
}

#c_right ul.matches.live {
border-top: 1px solid #414141;
border-bottom: 1px solid #414141;
box-shadow: 0 2px 0 #414141,0px -2px 0 #414141;
}

h2.comments {
border-bottom: 1px solid #1D1A13;
color: #B9B9B9;
}

.comment {
background: #414141;
color: #B9B9B9;
}

.comment .comment_head a {
color: #EBEBEB;
}

.comment .comment_buttons a {
color: #B9B9B9;
}

.forum_ed_quote {
color: #AFAFAF;
}

.forum_ed_quote {
background: #292929
}

.forum_ed_quote .forum_ed_quote {
background: #1D1A1A;
}

.forum_ed_quote .forum_ed_quote .forum_ed_quote {
background: #292929;
}

.forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote {
background: #1D1A1A;
}

.forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote {
background: #292929;
}

.forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote {
background: #1D1A1A;
}

.forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote {
background: #292929;
}

.forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote {
background: #1D1A1A;
}

.forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote {
background: #292929;
}

.forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote .forum_ed_quote {
background: #1D1A1A;
}

#comment_content {
color: #B9B9B9;
}

input[type=button], input[type=submit], a.button {
color: #202020;
}

.forum_post {
background: #414141;
color: #B9B9B9;
}

.forum_post .post {
color: #B9B9B9;
}

.forum_editor ul.controls li input {
opacity: .7;
}

.forum_ed_spoiler.open {
background: #303030;
}

table.group {
background: #1D1A13;
}

table.group tr th {
background: #414141;
}

table.group tr td {
background: #333;
}

table tr th {
color: #B9B9B9;
}

table tr td {
background: #414141;
}

.grid .round .match_container .match {
background: #333;
color: #B9B9B9;
}

.match_head {
color: #B9B9B9;
}

.match_head .cov {
color: #B9B9B9;
}

.match_up .c .top {
color: #B9B9B9;
}

ul.match_subs_heroes {
color: #B9B9B9;
}

#gallery_search, #vod_search {
background: #414141;
border: 1px solid #1D1A13;
color: #B9B9B9;
}

.gallery_head select, .vod_head select {
color: #B9B9B9;
}

.profile_details>span {
color: #B9B9B9;
}

.group_details span {
color: #B9B9B9;
}

ul.forum_nav li a {
border: 1px solid #414141;
}

.pagination ul li a {
border: 1px solid #414141;
}

.pagination ul li.disabled a {
border-color: #414141;
color: #B9B9B9;
}

#c_left ul.matches_streams li span.play {
opacity: .4;
}

input[type=button][disabled], input[type=submit][disabled], a.button[disabled] {
color: #101010;
}

div.msg.own {
    background: #101010;
}

.topnews-head .topnews-head-subtitle {
    color: #B9B9B9;
}

.topnews-head .topnews-head-time {
    color: #B9B9B9;
}

.topnews-select li {
    color: #B9B9B9;
    background: #1D1A13;
    border-bottom: 2px solid #101010;
}

.topnews-select li:not(.active):not(:last-child):after {
    background: #101010;
}

#c_left a.small {
    color: #B9B9B9;
}

}
