css:
@-moz-document domain("readmore.de") {
/* TITLES */

#c_content ul.breadcrumbs {
  margin: -12px -10px;
  padding: 12px 10px;
  background: #2b91ff;
  color: #f5f5f5;
}
#c_content ul.breadcrumbs a {
	color: #f5f5f5;
  opacity: 0.7;
}

.forum_thread_reply h2 {
  margin: 6px -10px;
  padding: 10px 10px;
  background: #2b91ff;
  color: #f6f6f6;  
}
.forum_post .head a {
  color: #f6f6f6; 
}
.forum_post .head {
  margin: -10px -10px 10px -10px;
  padding: 6px 10px;
  background: #2b91ff;
  color: #f6f6f6; 
}

#c_right h3 a, #c_left h3 a {
	color: #f5f5f5;
}
#c_right > .control, #c_left > .control {
  margin: 6px 0px;
  -webkit-filter: brightness(150%);
  filter: brightness(150%);
}
#c_right h3, #c_left h3 {
  	margin: 0px -8px 10px -8px;
  	padding: 6px;
	background: #2b91ff;
  	color: #f6f6f6; 
}
#c_right .headlines_cat {
	background: #dcdcdc;
}
#c_right .headlines_cat span a {

}
#c_right .headlines_cat .icon {
  -webkit-filter: brightness(90%);
  filter: brightness(90%);
}
#c_right ul li {
	border-color: #ddd;
  }
#c_right h3.mod, #c_left h3.mod {
	color: #b8d7f8;
  }

/* PAGINATION */
.pagination .active a {
  color: #f5f5f5;
  background: #2b91ff;	
}

#icon_miscellaneous_buttonScrollDown {
  margin-top: -39px;
}
#icon_miscellaneous_buttonScrollDown, #icon_miscellaneous_buttonScrollUp {
  border: none !important;
  color: white;
  background: #2b91ff;
}
body {background: #f5f5f5 !important;}
/*[[header]]*/
}

style_setting_options.value:
body {
    padding-top: 70px;
}
#header {
    position: fixed;
    z-index: 1337;
    top: 0;
}
